INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (660, 'INT', '', 'Описание Клюшка BAUER VAPOR 1X GRIPTAC Самая передовая и технологичная клюшка VAPOR на сегодняшний день. Созданная для молниеносных атак, VAPOR 1X с новой технологией Quick-Release Taper подарит вам новые ощущения на льду. Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: AERO SENSE 2 Core - NEW • Профиль крюка Pure Shot Форма рукоятки: • Круглая двояковогнутая рукоятка Конусность рукоятки • Технология сужения QRT (только SR и INT) - NEW Варианты захвата • Покрытие GRIPTAC • Также доступны варианты с матовой отделкой без зоны захвата Grip Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/501/400_400_140cd750bba9870f18aada2478b24840a/6b4107a9_0bd1_11e6_9fde_001fc6b58537_a66eb5a7_3914_11e6_bec3_dba2b096c3df.jpg?158329625614197', null, 'VAPOR 1X GRIPTAC', 1, 662, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR 1X GRIPTAC_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (352, 'JR', '', 'Описание Наплечник BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Наплечник обладает отличной мобильностью и его вес снижен примерно на 25% по сравнению с предыдущей моделью за счет использования в его конструкции облегченных материалов. Новейшая пена AEROLITE 2.0 имеет очень легкий вес и при этом не теряет своих защитных функций. Очень легкий композитный материал CURV® усиливает защитные свойства в стратегически важных местах. Внутренний материал выполнен с применением технологии 37,5®. Во время физических нагрузок ускоряется процесс испарения излишков влаги, что способствует быстрому высыханию наплечника. Помимо этого материал подкладки обрабатывается по технологии SANITIZED®, которая препятствует возникновению неприятных запахов в процессе игры. Особенности: • Уровень – Элитный • Защита плеч – AEROLITE 2.0 • Защита груди- CURV®; плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – CURV®; плавающая конструкция • Защита ключицы – HD FOAM; технология FREE FLEX • Защита бицепса – CURV®; технология FREE FLEX • Подкладка – Технология 37,5®; технология SANITIZED® Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b91/400_400_140cd750bba9870f18aada2478b24840a/68b5a794_6ae3_11e8_995b_3085a97fbce9_05ea5bc9_6fb9_11e8_995c_3085a97fbce9.jpg?158331397948026', null, 'VAPOR 1X LITE', 1, 354, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_VAPOR 1X LITE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (353, 'SR', '', 'Описание Наплечник BAUER SUPREME S17 S190 Защита Supreme спроектирована, чтобы обеспечить идеальную анатомическую посадку, которая даст максимальную энергию в каждом игровом эпизоде! Особенности: • Съемная защита живота • Защита груди: XRD и пена высокой плотности • Защита спины: пена высокой плотности со вставками из полиэтилена • Защита бицепса: пена средней плотности со вставками из полиэтилена • Термоформованые чашки с пеной средней плотности • Подкладка: THERMO MAX Нагрудник Supreme S190 предлагает профессиональный уровень защиты благодаря интегрированными накладками на плечах, формованному пенному наполнителю Vent Armor в области груди и пенным наполнителям средней плотности, защищающим грудь, живот и поясницу. В области спины используется пена высокой плотности с вставками из полиэтилена для обеспечения защиты в одном из наиболее уязвимых мест. Подкладка THERMO MAX испаряет пот с вашего тела, сохраняя прохладу и сухость во время игры. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/21d/400_400_140cd750bba9870f18aada2478b24840a/7a3cd36d_0d64_11e7_9c76_3085a97fbce9_444e3500_242e_11e7_9c82_3085a97fbce9.jpg?158331377553498', null, 'SUPREME S190', 1, 355, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME S190_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (354, 'SR', '', 'Описание Наплечник BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Наплечник обладает высоким уровень защиты и при этом имеет легкий вес. Ключевым изменением по сравнению с прошлой моделью стало внедрение плавающей конструкции защиты позвоночника, а также использование перфорированной пены со стороны спины. Особенности: • Уровень – Профессиональный • Защита плеч – Пена HYPERLITE • Защита груди – Пена HYPERLITE, плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – Пена HYPERLITE; плавающая конструкция • Защита ключицы – HD FOAM(пена высокой плотности); технология FREE FLEX • Защита бицепса – Пена HYPERLITE; технология FREE FLEX • Подкладка – TERMO MAX; технология SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/2b5/400_400_140cd750bba9870f18aada2478b24840a/68b5a79c_6ae3_11e8_995b_3085a97fbce9_05ea5bed_6fb9_11e8_995c_3085a97fbce9.jpg?158331227944480', null, 'VAPOR X900 LITE', 1, 356, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_VAPOR X900 LITE_SR.png');
