INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (666, 'SR', '', 'Описание Клюшка BAUER SUPREME 2S TEAM, полупрофессиональная клюшка в линейки SUPREME 2019 года. Благодаря своей конструкции FUSED 2-PIECE клюшка обладает высокой надежностью и прочностью. Геометрия шафта имеет квадратную форму. При изготовлении клюшки используется материал карбона UD. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. Крюк изготовлен из материала BIMAX 3K. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: • Уровень – Полупрофессиональный • Точка прогиба – Верхняя • Технология броска – MPK • Технология трубы – CARBON UD • Технология крюка - AERO FOAM I; BIMAX 3K; MAXBALANCE • Конструкция – 2-х составная • Геометрия шафта – Квадратная Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/882/400_400_140cd750bba9870f18aada2478b24840a/2ba65fa3_d827_11e8_9984_3085a97fbce9_2ba65fcb_d827_11e8_9984_3085a97fbce9.jpg?158329661120292', null, 'SUPREME 2S TEAM GRIPTAC', 1, 668, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME 2S TEAM GRIPTAC_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (802, 'SR', '', 'Описание Защита горла BAUER NG PREMIUM NLP20 Collar SR Данная модель выполнена из нити KEVLAR®. Данная нить максимально защищает ваше горло от порезов. Основные особенности: ✅Материал - KEVLAR® ✅Технология 37.5® испаряет влагу и обеспечивает максимальный комфорт ✅Размер - 34-43 см ✅Подходит на возраст примерно от 13 лет ✅Сертифицирована Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/78b/400_400_140cd750bba9870f18aada2478b24840a/d82c4168_0bd0_11e6_9fde_001fc6b58537_d2cc5d34_3abc_11e6_bec6_d5a05555e1dc.jpg?158331192348555', null, 'NG PREMIUM NLP20 COLLAR', 1, 804, 11, 'C:\Users\vinto/images/Защита шеи/BAUER_NG PREMIUM NLP20 COLLAR_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (356, 'SR', '', 'Описание Наплечник CCM ULTRA TACKS, верхняя модель в линейке CCM Tacks обладает легкой конструкцией и предлагает профессиональную защиту и мобильность. Передняя и задняя части нагрудника имеют двухпанельную конструкцию и сегментированное строение и улучшенную систему воздухопроницаемости, что положительным образом сказывается на комфорте. В качестве внутреннего наполнения в передней панели и в плавающем центральном щитке, защищающем грудину, используется пена высокой плотности. Плечевые чаши выполнены из формованного полиэтилена, имеют набивку из пены высокой плотности Dual Core. Кроме того в плечевых чашах используется специализированная пена D3Oоранжевого цвета. Этот защитный материал по-разному реагирует на силу удара. Пена остается мягкой и подвижной в обычном состоянии и при поглощении несильных ударов, в то время как при ударах высокой силы пена моментально твердеет, создавая эффективную защиту плечевого сустава, после чего снова возвращается в мягкое, гибкое состояние. Элементы защиты бицепса имеют двухсоставную конструкцию и выполнены из литой пены высокой плотности. Элемент защиты бицепса имеет съемную полиэтиленовую вкладку, создающую усиленную защиту. В зависимости от необходимого уровня защиты, игроки могут настроить посадку элементов защиты живота и бицепса или удалить их совсем. Задняя часть нагрудника имеет плавающую панель, закрывающую позвоночник и выполненную из пены высокой плотности. Усиленные элементы обеспечивают охватывающую защиту почек и нижних ребер. В качестве подкладки используется дополнительный слой армированной пены и гидрофобная, износоустойчивая микрофибра. Новая, улучшенная система связей Anti-lift System между элементами защиты позволяет им двигаться более свободно вместе с телом игрока. Смещение одного элемента не будет вызывать смещение соседнего элемента, например, при поднятии рук вверх защитная жилетка не будет смещаться. Для фиксации нагрудника используются вентилируемые, эластичные ремни на липучках, крепящиеся на талии. Упругие ремешки на липучках фиксируют элементы защиты бицепса. Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8da/400_400_140cd750bba9870f18aada2478b24840a/28f9b70d_0bd1_11e6_9fde_001fc6b58537_f2fe7ae3_180f_11e6_be99_b0939a30c1de.jpg?158329654441243', null, 'ULTRA TACKS', 2, 358, 4, 'C:\Users\vinto/images/Нагрудники/CCM_ULTRA TACKS_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (358, 'SR', '', 'Описание Наплечник BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена по сравнению с предыдущей коллекция BAUER SUPREME S170, теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – Формованная MD FOAM(пена средней плотности) ✅Защита груди – Формованная HD FOAM(пена высокой плотности) ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7a0/400_400_140cd750bba9870f18aada2478b24840a/d0384e21_9810_11e9_aa9a_a4bf0158c9f5_8566e1b4_99d9_11e9_aa9a_a4bf0158c9f5.jpg?158331284745855', null, 'SUPREME S29', 1, 360, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME S29_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (359, 'SR', '', '', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ec8/400_400_140cd750bba9870f18aada2478b24840a/b17cf4fe_4135_11e7_9c8f_3085a97fbce9_b17cf501_4135_11e7_9c8f_3085a97fbce9.jpg?158330883143867', null, 'SUPER TACKS', 2, 361, 4, 'C:\Users\vinto/images/Нагрудники/CCM_SUPER TACKS_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (360, 'SR', '', 'Описание Наплечник BAUER NEXUS 1N топовая модель в линейке Nexus 16 года. Традиционная посадка отлично сочетается с мультисегментной конструкцией нагрудника, гарантируя игроку высокий уровень подвижности защитного снаряжения и надежную защиту. Базовый слой нагрудника выполнен из пены средней плотности и разделен на сегменты для максимальной гибкости. Для создания дополнительной защиты ребер и области почек нагрудник усилен сегментами из пены высокой плотности. Подвижный щиток, закрывающий грудину, скомбинирован из пены высокой плотности, пластиковой вставки и формованной пены, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. В плечевых чашах, выполненных из литого пластика, так же используется пена с технологией Seven+, которая располагается в центре и обеспечивает эффективную защиту суставов. Съемная защита живота выполнена из пены средней плотности. Ворот нагрудника имеет мягкую окантовку из легкой пены, которая не травмирует кожу. Литой, двухсоставной, термоформируемый элемент защиты бицепса имеет свободную конструкцию Flex, которая обеспечивает максимальную мобильность этого элемента. Регулируемый ремень позволяет подобрать оптимальную длину элемента защиты бицепса. В задней части нагрудника используются перекрывающиеся сегменты из пены высокой плотности, которые проходят вдоль позвоночника, обеспечивая защиту профессионального уровня. Центральный щиток, закрывающий позвоночник, имеет широкую форму, перекрывая область сегментированной пены высокой плотности в базовом слое, что гарантирует высокий уровень защиты. Область ключиц и лопаток покрыта арочными элементами Free Glide Arch, при изготовлении которых используется пена высокой плотности. Арочные элементы движутся отдельно от базового слоя, обеспечивая игроку полную свободу движений. Внутренняя подкладка, выполненная по технологии 37.5, использует тепло тела хоккеиста для ускорения процесса испарения влаги и создания комфортного для игрока микроклимата. Специальная обработка материала подкладки исключает появление неприятных запахов. Эластичные ремни крепления с фиксацией на липучке позволяют закрепить нагрудник на талии и элементы защиты бицепса на руках игрока. Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ddb/400_400_140cd750bba9870f18aada2478b24840a/6edc06e0_48bc_11e6_b11e_a9e9ddf2845b_b02fdf68_53d3_11e6_9c01_3085a97fbce9.jpg?158327622953375', null, 'NEXUS 1N', 1, 362, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_NEXUS 1N_SR.png');
