INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (821, 'YTH', '', 'Описание Пояс для гетр BAUER GARTER BELT Пояс с креплениями служит для поддержки хоккейных гетр. Поясничная часть выполнена из упругой, прочной резинки с липучками на концах для регулировки объема. Набедренные резинки с фиксаторами регулируются по высоте. ✅Размер S/M подходит примерно на рост от 100 до 130 см ✅Объем S/M - 51см-58см ✅Размер L/XL подходит примерно на рост от 130 до 160 см ✅Объем L/XL - 56см-74см Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b52/400_400_140cd750bba9870f18aada2478b24840a/e80469eb_0bd0_11e6_9fde_001fc6b58537_00ebcbb1_3b53_11e6_bec6_d5a05555e1dc.jpg?158331023629324', null, 'GARTER BELT', 1, 823, 32, 'C:\Users\vinto/images/Аксессуары для игрока/BAUER_GARTER BELT_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (668, 'SR', '', 'Описание Клюшка BAUER VAPOR 1X GRIPTAC Самая передовая и технологичная клюшка VAPOR на сегодняшний день. Созданная для молниеносных атак, VAPOR 1X с новой технологией Quick-Release Taper подарит вам новые ощущения на льду. Профиль прогиба: • Профиль прогиба VAPOR Low-Kick Конструкция/технология изготовления рукоятки: • Система покрытия из смолы eLASTech (только SR и INT) • Технология Monocomp (монолитная клюшка) • Конструкция из углепластика TeXtreme® Конструкция/технология изготовления крюка: • Наружная часть: конструкция из углепластика TeXtreme® • Внутренняя часть: AERO SENSE 2 Core - NEW • Профиль крюка Pure Shot Форма рукоятки: • Круглая двояковогнутая рукоятка Конусность рукоятки • Технология сужения QRT (только SR и INT) - NEW Варианты захвата • Покрытие GRIPTAC • Также доступны варианты с матовой отделкой без зоны захвата Grip Длина производителя - 60 дюймов Полная длина клюшки - 166 см Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3f0/400_400_140cd750bba9870f18aada2478b24840a/6b4107b6_0bd1_11e6_9fde_001fc6b58537_d1cc79ea_3916_11e6_bec3_dba2b096c3df.jpg?158329682416601', null, 'VAPOR 1X LE GRIPTAC', 1, 670, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR 1X LE GRIPTAC_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (675, 'INT', '', 'Описание Клюшка BAUER SUPREME S17 1S GRIPTAC Революция свершилась. Долговечность, производительность и мощность берут верх. 2017 Supreme 1S с технологией RenewCore — это не просто ваш самый сильный бросок. Это ваш шанс начать забивать на совершенно ином уровне. Особенности: • Клюшка SUPREME с высокой точкой прогиба • Расширенная зона передачи энергии • Тактильная текстура улучшает безопасность и обеспечивает естественные ощущения • Технология eLASTech (только SR и INT) • Легкая конструкция TeXtreme • Технология Monocomp • Технология MPK • Наружная часть крюка: Технология восстановления хоккейного крюка RenewCore • Внутренняя часть крюка: Основа крюка POWER SENSE CORE 2 • Профиль крюка Pure Shot • Квадратная двояковогнутая рукоятка Характеристики Возрастная группа INT Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5d5/400_400_140cd750bba9870f18aada2478b24840a/90eb8b2b_818e_11e7_b06e_e0cb4ee6cf35_02cd6a7f_8322_11e7_9caa_3085a97fbce9.jpg?158331417316607', null, 'SUPREME S17 1S GRIPTAC', 1, 677, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME S17 1S GRIPTAC_INT.png');
