INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (363, 'JR', '', 'Описание Наплечник CCM SUPER TACKS 9080, профессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Новая система регулировки позволяет отрегулировать наплечник по объему. Для защиты груди используется пена D30 LITE, которая поглощает энергию удара и формованный полиэтилен. В области позвоночника для максимальной защиты используется пена средней плотности и пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара, с использованием пены D3O LITE. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – JDP; D3O LITE ✅Защита ключицы – Пена высокой плотности ✅Защита груди – D3O LITE; формованный полиэтилен ✅Передняя защита ребер – Пена высокой плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена высокой плотности Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7fb/400_400_140cd750bba9870f18aada2478b24840a/ad7a0f45_9811_11e9_aa9a_a4bf0158c9f5_87d37e15_99db_11e9_aa9a_a4bf0158c9f5.jpg?158331468947165', null, 'TACKS 9080', 2, 365, 4, 'C:\Users\vinto/images/Нагрудники/CCM_TACKS 9080_JR.png');
