INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (367, 'JR', '', 'Описание Наплечник CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости, мобильности и технологичности. Одна из главных технологий, которая будет использоваться в наплечники данной модели это очень легкий и жесткий композитный материал ROCKETFRAME. Защита плеч выполнена из пены высокой плотности U-FOAM. Новая конструкция внутренней части повторяет движения тела. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Элитный • Защита плеч – Конструкция UFOAM • Защита ключицы – Пена высокой плотности; плавающая конструкция • Защита груди – ROCKETFRAME • Передняя защита ребер - PREMIER ZOTEFOAM® • Защита позвоночника – ROCKETFRAME • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/987/400_400_140cd750bba9870f18aada2478b24840a/843c646a_88c0_11e8_995e_3085a97fbce9_843c646b_88c0_11e8_995e_3085a97fbce9.jpg?158331062741424', null, 'JETSPEED FT1', 2, 369, 4, 'C:\Users\vinto/images/Нагрудники/CCM_JETSPEED FT1_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (368, 'SR', '', 'Описание Наплечник BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Наплечник обладает отличной мобильностью и его вес снижен примерно на 25% по сравнению с предыдущей моделью за счет использования в его конструкции облегченных материалов. Новейшая пена AEROLITE 2.0 имеет очень легкий вес и при этом не теряет своих защитных функций. Очень легкий композитный материал CURV® усиливает защитные свойства в стратегически важных местах. Внутренний материал выполнен с применением технологии 37,5®. Во время физических нагрузок ускоряется процесс испарения излишков влаги, что способствует быстрому высыханию наплечника. Помимо этого материал подкладки обрабатывается по технологии SANITIZED®, которая препятствует возникновению неприятных запахов в процессе игры. Особенности: • Уровень – Элитный • Защита плеч – AEROLITE 2.0 • Защита груди- CURV®; плавающая конструкция • Передняя защита ребер – Формованная пена HYPERLITE • Защита спины – Пена HYPERLITE в области ребер; перфорированная пена • Защита позвоночника – CURV®; плавающая конструкция • Защита ключицы – HD FOAM; технология FREE FLEX • Защита бицепса – CURV®; технология FREE FLEX • Подкладка – Технология 37,5®; технология SANITIZED® Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f21/400_400_140cd750bba9870f18aada2478b24840a/ea8cf6d6_6236_11e8_bc17_e0cb4ee6cf35_05ea5bc5_6fb9_11e8_995c_3085a97fbce9.jpg?158329345848026', null, 'VAPOR 1X LITE', 1, 370, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_VAPOR 1X LITE_SR.png');
