INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (888, 'SR', '', 'Описание Стелька Bauer Speed Plate 2.0 имеет противоскользящую поверхность и изготовлена из полностью термоформуемого композита. Для увеличения передачи энергии предусмотрена анатомическая вставка в области плюсневой кости, а пена средней плотности покрывает области с высоким уровнем воздействия, чтобы обеспечить непревзойденный комфорт. Стелька Bauer Speed Plate это персональное удобство, минимум негативного пространства, максимальный контакт стопы с поверхностью стельки и резкое ускорение. Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5f2/400_400_140cd750bba9870f18aada2478b24840a/0daedeba_623b_11e8_9958_3085a97fbce9_61732d0d_66a2_11e8_9959_3085a97fbce9.jpg?158327700156849', null, 'SPEED PLATE 2.0', 1, 890, 29, 'C:\Users\vinto/images/Аксессуары для коньков/BAUER_SPEED PLATE 2.0_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (376, 'SR', '', 'Описание Наплечник BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди для максимальной защиты используется композитный материал CURV, который придает наплечнику более легкий вес и максимальную защиту. В качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения, а пена PORON XRD, которая использована в них, служит для максимального поглощения энергии удара. Для максимальной подвижности и комфорта в области бицепса и ребер используется конструкция AMP. В качестве внутренней подкладки используется материал THERMO CORE, который максимально хорошо отводит влагу. Особенности: ✅Уровень – Элитный ✅Защита плеч – PORON XRD ✅Защита груди – CURV ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – VENT ARMOR ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO CORE Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6b2/400_400_140cd750bba9870f18aada2478b24840a/b8aa9a09_77aa_11e9_aa99_a4bf0158c9f5_9ebc39d6_99d9_11e9_aa9a_a4bf0158c9f5.jpg?158330947661672', null, 'SUPREME 2S PRO', 1, 378, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME 2S PRO_SR.png');
