INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (405, 'YTH', '', 'Описание Перчатки BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки. Манжета имеет сегментированное строение, благодаря чему ребенок может двигать запястьем свободно. Для максимального удобства и защиты тыльная сторона перчатки имеет сегментированное строение из пены средней плотности. Пальцы у перчаток имеют двухсоставную конструкцию, обеспечивая максимальный комфорт во время движения. Ладонь, выполненная из материала NASH с вставками из не скользящего материала в области пальцев. Внутренняя подкладка THERMO MAX+ сохраняют руки в сухости и придают максимальный комфорт. Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Внешний материал – Плетеная сетка CABLE MESH; Нейлон ✅Защита тыльной стороны – Пена средней плотности ✅Защита пальцев – Пена средней плотности ✅Большой палец – Цельный, неподвижный ✅Ладонь – NASH ✅Подкладка – THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5f7/400_400_140cd750bba9870f18aada2478b24840a/8a987e01_7638_11e9_aa99_a4bf0158c9f5_8e4691ed_87ff_11ea_aa9e_a4bf0158c9f5.jpg?158795197674173', null, 'SUPREME 2S PRO', 1, 407, 12, 'C:\Users\vinto/images/Перчатки/BAUER_SUPREME 2S PRO_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (482, 'JR', '', 'Описание Трусы CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Данные трусы имеют очень легкий вес и зауженную посадку, для лучшей мобильности и комфорта. В стратегически важных местах, таких как, тазобедренные суставы, позвоночник, бедро используется полиэтилен, который максимально защищает от ударов. Внутренняя подкладка DRYFORM, которая эффективно отталкивает влагу. Чтобы подобрать идеальную форму шорты имеют регулируемую посадку и могут удлиняться на 1« (2,5 см) за счет вшитой под поясом молнии. Чехол трусов выполнен из высокопрочного текстурированного полиэстера плотностью 400 ден и усилен боковыми вставками, в области бедра. Особенности: • Уровень – Профессиональный • Удобство – Зауженное • Защита позвоночника – Формованные вставки PE • Защита почек – Формованные вставки PE • Защита тазобедренная – Формованные вставки PE • Защита бедра – Формованные вставки PE • Подкладка – DRYFOAM • Регулировка – 1 дюйм (2.5см) • Чехол – Плотность 400 ден; усиленные вставки в области бедра Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/598/400_400_140cd750bba9870f18aada2478b24840a/2d5d207c_08f8_11e9_999c_3085a97fbce9_2d5d207d_08f8_11e9_999c_3085a97fbce9.jpg?158331323740182', null, 'JETSPEED FT390', 2, 484, 9, 'C:\Users\vinto/images/Трусы/CCM_JETSPEED FT390_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (688, 'SR', '', 'Описание Клюшка CCM RIBCOR TRIGGER 3D, топовая модель линейки CCM 2018 года. Клюшка имеет низкую точку прогиба OPTIMIZED LOW KICK. При производстве клюшки используется углеродное волокно SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая геометрия трубы POP MATRIX, которая улучшает передачу энергии и эффективность клюшки. Крюк имеет конструкцию ASCENT BLADE 3 , которая обеспечивает превосходное ощущение и высокую точность броска. Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология трубы – X-FLOW; POP MATRIX • Технология крюка – ASCENT BLADE 3 • Материал - SIGMATEX Характеристики Возрастная группа SR Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/bdb/400_400_140cd750bba9870f18aada2478b24840a/83028efd_f306_11e8_9991_3085a97fbce9_83028f01_f306_11e8_9991_3085a97fbce9.jpg?158329670319822', null, 'RIBCOR TRIGGER 3D PMT GRIP', 2, 690, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_RIBCOR TRIGGER 3D PMT GRIP_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (691, 'SR', '', 'Описание Клюшка BAUER SUPREME 2S, профессиональная клюшка в линейки SUPREME. Главным преимуществом клюшки является эффективность и прочность. Чтобы сделать эту клюшку самой прочной в серии SUPREME производитель укрепил стенки рукоятки. Клюшка выполнена из углеродного волокна 3К, благодаря этому клюшка очень прочна. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. Материал крюка углеродное волокно 12К.Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. В качестве внутреннего сердечника крюка используется пена AERO FOAM I, которая лучше поглощает энергию и способствует снижению вибрации. Длина производителя - 64 дюйма Полная длина клюшки - 175 см Особенности: ✅Уровень – Профессиональный ✅Точка прогиба – Верхняя ✅Технология броска – MPK ✅Технология трубы – Карбон 3К ✅Технология крюка – Карбон 12К; MaxBalance; AERO FOAM I ✅Конструкция – High Performance mold ✅Геометрия шафта – Квадратная Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/90e/400_400_140cd750bba9870f18aada2478b24840a/fd33c7d0_932c_11e9_aa9a_a4bf0158c9f5_d91cd65e_9465_11e9_aa9a_a4bf0158c9f5.jpg?158331056518863', null, 'SUPREME 2S GRIPTAC', 1, 693, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME 2S GRIPTAC_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (391, 'YTH', '', 'Описание Перчатки BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Данные перчатки предназначены для детей начинающего уровня. Перчатки имеют удобную посадку и обеспечивают хорошую защиту. Защитные сегменты выполнены из пены двойной плотности. Гибкая манжета позволяет запястью свободно двигаться. Внутренняя подкладка из гидрофобной сетки способствует быстрому высыханию. Особенности: ✅Уровень – Любительский ✅Удобство – Клиновидное ✅Внешний материал – Высокопрочная нить CABLE MESH ✅Защита тыльной стороны – Пена средней плотности ✅Защита пальцев – Пена средней плотности ✅Ладонь – NASH ✅Подкладка – HYDROPHOBIC Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8bf/400_400_140cd750bba9870f18aada2478b24840a/050284a2_9813_11e9_aa9a_a4bf0158c9f5_36adf1db_99dc_11e9_aa9a_a4bf0158c9f5.jpg?158331299658221', null, 'NSX', 1, 393, 12, 'C:\Users\vinto/images/Перчатки/BAUER_NSX_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (392, 'YTH', '', 'Описание Перчатки CCM SUPER TACKS AS1 YTH, детская топовая модель линейки TACKS 2019 года. Данные перчатки разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Перчатки имеют анатомическую плотную посадку в области пальцев и обратной стороны ладони, но в верхней части манжета сделана более широкой, чтобы ребенку было удобнее работать с клюшкой. Внешний материал перчатки выполнен из высококачественного полиэстера, сочетающего высокую износостойкость, низкий вес и отличную гибкость. Для обеспечения максимального уровня защиты в области пальцев и тыльной стороны перчатки используется пена D3O, которая поглощает энергию удара и пластик. Ладонь выполнена из материала NASH, что гарантирует отличное чувство клюшки и комфорт во время игры. Особенности: ✅Уровень – Элитный ✅Внешний материал – Высококачественный полиэстер ✅Защита тыльной стороны – D3O; Вставки из пластика PE ✅Ладонь – NASH Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8dc/400_400_140cd750bba9870f18aada2478b24840a/03b153a4_78a5_11e9_aa99_a4bf0158c9f5_87bf2205_99e2_11e9_aa9a_a4bf0158c9f5.jpg?158328478161855', null, 'SUPER TACKS AS1', 2, 394, 12, 'C:\Users\vinto/images/Перчатки/CCM_SUPER TACKS AS1_YTH.png');
