INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (407, 'SR', '', 'Описание Перчатки CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Данная модель выполнена из высококачественного сетчатого материала PRO MESH, который придает перчаткам прочность и легкость. Для защиты тыльной части и пальцев используется вставки из полиэтилена и пластика. Материал ладошки NASH с дополнительной накладкой. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Высококачественный сетчатый материал PRO MESH • Защита тыльной стороны – Полиэтилен; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – NASH • Подкладка – Полиуретан PU Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/dd7/400_400_140cd750bba9870f18aada2478b24840a/843c647d_88c0_11e8_995e_3085a97fbce9_843c647e_88c0_11e8_995e_3085a97fbce9.jpg?158329261372950', null, 'JETSPEED FT370', 2, 409, 12, 'C:\Users\vinto/images/Перчатки/CCM_JETSPEED FT370_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (693, 'SR', '', 'Описание Клюшка BAUER VAPOR 2X, профессиональная модель линейки VAPOR 2019 года. Данная клюшка выполнена из углеродного волокна 3К, благодаря этому клюшка является достаточно прочной и легкой. Одним из ключевых нововведений стала технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Клюшка имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD. Крюк клюшки выполнен из углеродного волокна 12K, благодаря этому достигается идеальный баланс клюшки. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: ✅Уровень – Профессиональный ✅Точка прогиба – Нижняя ✅Технология броска – XE TAPER ✅Технология трубы – Карбон 3К ✅Технология крюка – Карбон 12К ✅Конструкция – High Performance mold ✅Геометрия шафта – С закругленными краями Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/91a/400_400_140cd750bba9870f18aada2478b24840a/5d8463d7_e5a5_11e9_aa9a_a4bf0158c9f5_c2414493_74c2_11ea_aa9c_a4bf0158c9f5.jpg?158618538017102', null, 'VAPOR 2X GRIP', 1, 695, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR 2X GRIP_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (698, 'INT', '', 'Описание Клюшка WARRIOR COVERT QRE, топовая модель линейки COVERT 2018 года. Особенности: • EDGE QUICK RELEASE – Клюшки данной серии имеют максимально низкую точку прогиба SUPER LOW KICK для быстрых и резких бросков • EDGE TAPER – Уникальный профиль клюшки обеспечивает один из самых быстрых и точных бросков, благодаря ромбовидной форме сужения клюшки • MINIMUS CARBON 1200 - Высокопрочная, надежная и легкая конструкция из цельнолитого углеродистого волокна. Новая геометрия края клюшки в момент броска, мгновенно и точно передает энергию на крюк, бросок получается более резкий и мощный. • TRUE 1 PHANTOM – Клюшка имеет полностью монолитную конструкцию и отличается хорошей сбалансированностью и прочностью, увеличивает чувство владения клюшкой во время игры, дает вам больше контроля и реакции • PRO CORTEX GRIP – Нескользящее текстурированное мягкое покрытие клюшки- легко удерживать в руках для оптимального контроля Характеристики Возрастная группа INT Линейка COVERT', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a3c/400_400_140cd750bba9870f18aada2478b24840a/2749460e_2630_11ea_aa9a_a4bf0158c9f5_389dec57_2b9f_11ea_aa9a_a4bf0158c9f5.jpg?158329317819924', null, 'COVERT QRE GRIP', 3, 700, 14, 'C:\Users\vinto/images/Клюшки игрока/WARRIOR_COVERT QRE GRIP_INT.png');
