INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (707, 'SR', '', 'Описание Клюшка CCM SUPER TACKS AS2 PRO, топовая модель линейки TACKS 2019 года. Данная клюшка выполнена из углеродного волокна SIGMATEX, которое было разработано мировым лидером в области аэрокосмических технологий специально для ССМ. Данное волокно имеет особое плетение, обеспечивая прочность и легкость клюшки. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Клюшка имеет среднюю точку прогиба, обеспечивая лучшую стабильность и контроль во время мощных бросков. Крюк ACU4 имеет легкую конструкцию и обеспечивает максимальный контроль и ощущение во время броска. Так же крюк имеет усиленную пятку и носок для максимальной прочности. Длина клюшки: ☑70 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑75 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑80 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑85 FLEX – Длина производителя - 60 дюймов; полная длина - 169 см ☑95 FLEX – Длина производителя - 62 дюйма; полная длина - 175 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Средняя ✅Технология трубы – X-FLOW ✅Технология крюка – ACU4 ✅Материал - SIGMATEX Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8e1/400_400_140cd750bba9870f18aada2478b24840a/23549c38_ffd2_11e9_aa9a_a4bf0158c9f5_85f4a101_8280_11ea_aa9d_a4bf0158c9f5.jpg?158739080419147', null, 'SUPER TACKS AS2 PRO GRIP', 2, 709, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_SUPER TACKS AS2 PRO GRIP_SR.png');
