INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (416, 'JR', '', 'Описание Перчатки CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Тыльная сторона перчатки для максимальной защиты, выполнена из пены высокой плотности и пластиковых вставок PE, которые имеют очень легкий вес и хорошие защитные функции. Ладонь выполнена из материала CLARINO с дополнительной вставкой, которая обладает хорошей износостойкостью, а специально, разработанный рельефный узор служит для лучшего сцепления и чувствительности клюшки. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – Высококачественный полиэстер • Защита тыльной стороны – Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь – CLARINO • Подкладка – Полиуретан PU; POLYGIENE Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/442/400_400_140cd750bba9870f18aada2478b24840a/fe43db36_af54_11e8_9963_3085a97fbce9_fe43db37_af54_11e8_9963_3085a97fbce9.jpg?158331381183910', null, 'JETSPEED FT390', 2, 418, 12, 'C:\Users\vinto/images/Перчатки/CCM_JETSPEED FT390_JR.png');
