INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (422, 'JR', '', 'Описание Перчатки CCM TACKS 9080, профессиональная модель линейки CCM 2019 года. Перчатки TACKS 9080 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из очень легкого и прочного материала PRO SELECTED POLYESTER. Защита пальцев и тыльной стороны перчатки выполнена из пены высокой плотности, технологии D3O,которая максимально поглощает энергию, и полиэтиленовых вставок. Ладонь выполнена из материала PALM CLARINO, которая обладает максимальным удобством, а дополнительная накладка из армированного кевлара, обеспечивает прочность. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Профессиональный • Внешний материал – PRO SELECTED POLYESTER • Защита тыльной стороны – D3O; Пена высокой плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –PALM CLARINO • Подкладка – POLYGIENE Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/725/400_400_140cd750bba9870f18aada2478b24840a/c7fb7a03_9813_11e9_aa9a_a4bf0158c9f5_1f5d0055_99e2_11e9_aa9a_a4bf0158c9f5.jpg?158331147369661', null, 'TACKS 9080', 2, 424, 12, 'C:\Users\vinto/images/Перчатки/CCM_TACKS 9080_JR.png');
