INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (458, 'YTH', '', 'Описание Трусы BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет классическую посадку. Для защиты позвоночника используется пена высокой плотности, а в области почек используется пена средней плотности с вставкой из пластика. Защита копчика имеет сегментированное строение, выполнена из пены IX-FOAM, которая максимально поглощает энергию удара. Внешний материал трусов выполнен из износостойкого нейлона плотностью 400 ден. В качестве внутренней подкладки используется THERMO MAX+, который максимально отводит влагу и обеспечивает комфорт. Одно из нововведения в этих трусах, стало то, что их можно отрегулировать на 1 дюйм(2,5см) Особенности: ✅Уровень – Профессиональный ✅Удобство – Классическая ✅Защита позвоночника – Пена высокой плотности HD ✅Защита почек – Пена средней плотности MD; пластиковые вставки ✅Защита тазобедренная – MD FOAM; пластиковые вставки ✅Защита копчика – IX-FOAM ✅Подкладка – THERMO MAX+ ✅Регулировка – 1 дюйм (2.5см) ✅Чехол – Плотность 400 ден Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b9f/400_400_140cd750bba9870f18aada2478b24840a/0bbbf173_90db_11e9_aa9a_a4bf0158c9f5_2b30e9a0_99dd_11e9_aa9a_a4bf0158c9f5.jpg?158329832436909', null, 'SUPREME 2S PRO', 1, 460, 9, 'C:\Users\vinto/images/Трусы/BAUER_SUPREME 2S PRO_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (462, 'YTH', '', 'Описание Трусы CCM TACKS AS1 YTH, детская профессиональная модель линейки TACKS 2019 года. Данный трусы разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Внешний материал трусов выполнен из нейлона плотностью 200 ден с усилением в ключевых зонах нейлоном плотностью 400 ден. Система регулировки по длине с помощью замка-молнии позволяет вам удлинить трусы на 1 дюйм(2,5см). Для обеспечения высокого уровня защиты в области копчика используется пена D3O, которая максимально поглощает энергию удара. Для защиты позвоночника, почек и тазобедренных суставов используется пена высокой плотности и пластик. Особенности: ✅Уровень – Профессиональный ✅Удобство – Анатомическое ✅Защита позвоночника – Пена высокой плотности ✅Защита почек – Пластик ✅Защита тазобедренная – Пена высокой плотности; Пластик ✅Защита бедра – Пластик; Пена высокой плотности ✅Защита копчика – D3O ✅Регулировка – 1 дюйм (2,5см) ✅Чехол – 200 ден с усилением в ключевых зонах нейлоном плотностью 400 ден Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/844/400_400_140cd750bba9870f18aada2478b24840a/8fb9a12d_78a8_11e9_aa99_a4bf0158c9f5_bf790815_79be_11e9_aa99_a4bf0158c9f5.jpg?158328330536954', null, 'SUPER TACKS AS1', 2, 464, 9, 'C:\Users\vinto/images/Трусы/CCM_SUPER TACKS AS1_YTH.png');
