INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (503, 'YTH', '', 'Описание Наколенники CCM TACKS 9040 YTH, детская любительская модель линейки TACKS 2019 года. Данная модель предназначена для детей начального уровня. Наколенники имеют полностью симметричную дизайн без деления на правый и левый. Коленная чаша выполнена из пластика с применением дополнительного слоя пены изнутри, что позволяет избежать травм при падении. Защита голени выполнена из пластика с ребрами жесткости, что повышает ее защитные свойства. Надежная фиксация щитков на ноге возможна благодаря двум ремням. Особенности: ✅Уровень – Любительский ✅Защита колена - пластик с применением дополнительного слоя пены изнутри ✅Защита голени - Пластик ✅Крепление – Два ремня Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/14c/400_400_140cd750bba9870f18aada2478b24840a/1be8b7c6_78b0_11e9_aa99_a4bf0158c9f5_1548e43c_7ad4_11e9_aa99_a4bf0158c9f5.jpg?158328753948135', null, 'TACKS 9040', 2, 505, 5, 'C:\Users\vinto/images/Щитки/CCM_TACKS 9040_YTH.png');
