INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (511, 'YTH', '', 'Описание Наколенники CCM SUPER TACKS AS1 YTH, детская профессиональная модель линейки TACKS 2019 года. Данные наколенники разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Область колена и голени выполнена из формованного полиэтилена высокого качества, благодаря этому наколенники имеют легкий вес и хорошие защитные функции. Для максимального удобства и комфорта используется два ремня регулировки. Съемный внутренний вкладыш с пеной D3O в области коленной чашки, максимально защищает от прямых ударов и остается сухим во время игры. Особенности: ✅Уровень – Профессиональный ✅Защита колена – D3O ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e94/400_400_140cd750bba9870f18aada2478b24840a/197517fb_78a8_11e9_aa99_a4bf0158c9f5_aeaf8ec3_79b1_11e9_aa99_a4bf0158c9f5.jpg?158328755165352', null, 'SUPER TACKS AS1', 2, 513, 5, 'C:\Users\vinto/images/Щитки/CCM_SUPER TACKS AS1_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (517, 'SR', '', 'Описание Наколенники CCM TACKS 9040, любительская модель линейки TACKS 2019 года. Данная модель имеют анатомическую посадку. Коленная чаша выполнена по технологии JDP, которая рассеивает энергию и защищает ногу от сильных ударов. В области икроножной мышцы для больше защиты используется пена средней плотности и пластик. Для максимального удобства в наколенниках используется новая система регулировки, благодаря чему его можно максимально отрегулировать по объему. Особенности: ✅Уровень – Любительский ✅Защита икры – Пена средней плотности; пластик ✅Защита бедра – Пена средней плотности ✅Крепление – Два ремня; в области икроножной мышцы возможность отрегулировать по длине Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/84d/400_400_140cd750bba9870f18aada2478b24840a/ad9b9949_78a3_11e9_aa99_a4bf0158c9f5_a43199e5_79b2_11e9_aa99_a4bf0158c9f5.jpg?158328777355676', null, 'TACKS 9040', 2, 519, 5, 'C:\Users\vinto/images/Щитки/CCM_TACKS 9040_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (522, 'JR', '', 'Описание Наколенники BAUER NEXUS 1N - топовая модель в линейке NEXUS 16 года. В щитках 1N увеличена ширина защиты голени. Кроме того, в щитке используется встроенная накладка для защиты коленной чашечки и термоформируемая накладка для защиты голени. Защита икры — это та часть, где вы снова увидите использование технологии Seven + вместе с дополнительным PE вкладышем. Протектор бедра и основной вкладыш состоят из пены HD, в то время как боковые «крылья» на колене выполнены из пены MD с формованной PE вставкой. Фиксирует щиток система двойных ремней — ремень, который оборачивается вокруг щитка и новая регулируемая конструкция Bauer Tune Fit. Tune Fit обеспечивает более широкий диапазон для кастомизированной посадки, чтобы предотвратить перемещение или движение щитка во время игры. Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b01/400_400_140cd750bba9870f18aada2478b24840a/b02fdf5e_53d3_11e6_9c01_3085a97fbce9_b02fdf5f_53d3_11e6_9c01_3085a97fbce9.jpg?158329697649126', null, 'NEXUS 1N', 1, 524, 5, 'C:\Users\vinto/images/Щитки/BAUER_NEXUS 1N_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (525, 'JR', '', 'Описание Наколенники BAUER SUPREME S17 1S Скользите быстрее и эффективнее с новыми щитками Supreme 1S! В щитках Supreme 1S впервые применен композитный материал Curv®, который в сочетании с профилированной пеной POWERLITE, обеспечивает максимальное прилегание к телу, сохраняя при этом исключительный уровень защиты и минимальный вес. Особенности: • Общая посадка : Анатомическая форма • Конструкция: Многосегментный дизайн • Система крепления: 2 ремня с технологией Anchor Strap • Подкладка: 37,5® Technology и Sanitized® • Щиток на голень: Curv®и POWERLITE • Наколенник: Интегрированный наколенник • Внутренняя часть колена: FleXorb, G-FORM® и XRD®Material • Защита икр: профилированная пена средней плотности Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/69f/400_400_140cd750bba9870f18aada2478b24840a/7a3cd386_0d64_11e7_9c76_3085a97fbce9_1938f44b_869c_11e7_9cab_3085a97fbce9.jpg?158327618969811', null, 'SUPREME 1S', 1, 527, 5, 'C:\Users\vinto/images/Щитки/BAUER_SUPREME 1S_JR.png');
