INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (549, 'SR', '', 'Описание Наколенники BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Наколенники имеют анатомическую форму. В данной модели применена технология AMP, для лучшей сегментации элементов щитка, обеспечивающей свободу движения. В области икры используется композитный материал CURV, который обеспечивает более легкий вес и максимальную защиту. Для защиты бедра используется пена высокой плотности, а в области коленной чашечки имеется пена PORON XRD, которая максимально поглощает энергию удара. Внутренняя подкладка THERMO CORE, которая эффективно отводит влагу и обеспечивает комфорт. Особенности: ✅Уровень – Элитный ✅Посадка - Анатомическая ✅Защита икры – CURV ✅Боковая защита – VENT ARMOR ✅Защита колена – PORON XRD ✅Защита бедра – Пена высокой плотности ✅Крепление – Два ремня фиксируются сверху и снизу ✅Подкладка – THERMO CORE Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d84/400_400_140cd750bba9870f18aada2478b24840a/fab082b8_77aa_11e9_aa99_a4bf0158c9f5_33309433_99a9_11e9_aa9a_a4bf0158c9f5.jpg?158329311161627', null, 'SUPREME 2S PRO', 1, 551, 5, 'C:\Users\vinto/images/Щитки/BAUER_SUPREME 2S PRO_SR.png');
