INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (592, 'YTH', '', 'Описание Клюшка CCM JETSPEED 440 GRIP YTH, детская любительская модель линейки JETSPEED 2019 года. Клюшка данной модели имеет гибридную точку прогиба, что гарантирует эффективную работу рукоятки при выполнении броска любого типа. Ее универсальность делает клюшку отличным выбором для юного хоккеиста. Крюк JS3 имеет легкий вес и обеспечивает более точный бросок и передачу. Длина производителя - 43 дюйма Полная длина клюшки - 124 см Длина клюшки: ☑20 FLEX – Длина 43 дюйма Особенности: ✅Уровень – Любительский ✅Точка прогиба – Гибридная ✅Технология крюка – JS3 Характеристики Возрастная группа YTH Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/93f/400_400_140cd750bba9870f18aada2478b24840a/2a400dc8_b439_11e9_aa9a_a4bf0158c9f5_f5a3a6fb_b9e0_11e9_aa9a_a4bf0158c9f5.jpg?158331282315188', null, 'JETSPEED 440 GRIP', 2, 594, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_JETSPEED 440 GRIP_YTH.png');
