INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (692, 'JR', '', 'Описание Клюшка BAUER SUPREME 2S PRO, топовая клюшка в линейки SUPREME. Клюшка выполнена из легкого, высококачественного углеродного волокна TeXtreme по технологии MONOCOMP, в результате чего нет перехода между трубой и пером, делая клюшкой очень отзывчивой и легкой. В шафте используется смола нового поколения DURAFLEX , которая увеличивает прочность и долговечность. Данная клюшка имеет увеличенную, высокую точку прогиба с применением технологии MAX POWER KICK(MPK), благодаря чему бросок становиться более резким и мощным. В крюке данной модели содержится гель RENEWCORE, который заполняет микротрещины и повышает прочность и долговечность клюшки. При производстве крюка так же используется технология ADVANCED CARBON LAYERING, обеспечивая более высокую плотность между слоями углеродного волокна, в результате чего уменьшается количество смолы, пропитывающей углеродное волокно, что приводит к снижению веса клюшки и лучшему сопротивлению ударным нагрузкам. Длина производителя - 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: • Уровень – Элитный • Точка прогиба – Верхняя • Технология броска – MPK • Технология трубы – TeXtreme; DURAFLEX • Технология крюка - TeXtreme; ACL • Конструкция – MONOCOMP • Геометрия шафта – Квадратная Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3f1/400_400_140cd750bba9870f18aada2478b24840a/2ba65fa1_d827_11e8_9984_3085a97fbce9_2ba65fad_d827_11e8_9984_3085a97fbce9.jpg?158330641520345', null, 'SUPREME 2S PRO GRIPTAC', 1, 694, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_SUPREME 2S PRO GRIPTAC_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (695, 'INT', '', 'Описание Клюшка CCM RIBCOR TRIGGER 4 PRO, топовая модель линейки CCM 2019 года. При производстве клюшки используется высокотехнологичное углеродное волокно SIGMATEX 18К, делая клюшку невероятно легкой и прочной. Данная клюшка выполнена по технологии X-FLOW, которая обеспечивает эффективное устранение излишков смолы и пустот в стенке рукоятки, что снижает ее вес и гарантирует монолитность, прочность и лучшую стабильность при эксплуатации. Новая конструкция ERGONOMIC SHAFT DESIGN обеспечивает разную форму шафта по всей длине клюшки, придавая максимальное удобство и комфорт во время игры. Клюшка имеет нижнюю точку прогиба с новым ассиметричным тэйпером, который предполагает традиционную вогнутость с внутренней стороны, обеспечивая максимальную загрузку клюшки и наличие выпуклой геометрии с внешней стороны тэйпера для большей точности бросков и контроля шайбы. Новый крюк ADGILITY BLADE имеет мягкую конструкцию для более быстрых и резких бросков. ☑55 FLEX – Длина производителя - 55 дюймов; полная длина - 157 см ☑65 FLEX – Длина производителя - 57 дюймов; полная длина - 162 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология трубы – X-FLOW; ERGONOMIC SHAFT DESIGN ✅Технология крюка – ADGILITY BLADE ✅Материал – SIGMATEX 18К Характеристики Возрастная группа INT Линейка RIBCOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d10/400_400_140cd750bba9870f18aada2478b24840a/72daf0c8_11e1_11ea_aa9a_a4bf0158c9f5_e0a9c2c2_1b1f_11ea_aa9a_a4bf0158c9f5.jpg?158328281918615', null, 'RIBCOR TRIGGER 4 PRO GRIP', 2, 697, 14, 'C:\Users\vinto/images/Клюшки игрока/CCM_RIBCOR TRIGGER 4 PRO GRIP_INT.png');
