INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (699, 'SR', '', 'Описание Клюшка BAUER NEXUS 2N, профессиональная клюшка в линейки NEXUS. Главным преимуществом клюшки является эффективность и прочность. Клюшка выполнена из высококачественного углеродного волокна 12К и имеет двухсоставную конструкцию HIGH PERFORMANCE MOLD, благодаря этому клюшка считается очень отзывчивой, легкой и прочной. Данная клюшка имеет среднюю точку прогиба с технологией SWEET-SPOT, что делает ее идеальной для игроков, стремящихся получить более точный и мощный бросок. Крюк данной модели выполнен по технологии MaxBalance, за счет этого уменьшается вес клюшки. Длина производителя - 64 дюйма Полная длина клюшки - 175 см Особенности: • Уровень – Профессиональный • Точка прогиба – Средняя • Технология броска – SWEET-SPOT • Технология трубы – Карбон 12К • Технология крюка – Карбон 12К; MaxBalance; R-LITE • Конструкция – HIGH PERFORMANCE MOLD Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/33f/400_400_140cd750bba9870f18aada2478b24840a/c665b29b_932c_11e9_aa9a_a4bf0158c9f5_98c53cdc_9465_11e9_aa9a_a4bf0158c9f5.jpg?158330671219386', null, 'NEXUS 2N GRIPTAC', 1, 701, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_NEXUS 2N GRIPTAC_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (701, 'JR', '', 'Описание Клюшка BAUER VAPOR FLYLITE JR, топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE! Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 30 FLEX - 50 дюймов; 40 FLEX - 52 дюйма; 50 FLEX - 54 дюйма Полная длина клюшки - 30 FLEX - 139 см; 40 FLEX - 145 см; 50 FLEX - 150 см Особенности: ✅Уровень – Элитный ✅Точка прогиба – Нижняя ✅Технология броска – XE TAPER ✅Технология трубы – TeXtreme; DURAFLEX; ACL ✅Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE ✅Конструкция – MONOCOMP ✅Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d74/400_400_140cd750bba9870f18aada2478b24840a/3b94210c_8ce5_11e9_aa99_a4bf0158c9f5_694ed9a7_c9dd_11e9_aa9a_a4bf0158c9f5.jpg?158332150420705', null, 'VAPOR FLYLITE GRIP', 1, 703, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR FLYLITE GRIP_JR.png');
