INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (717, 'SR', '', 'Описание Клюшка BAUER VAPOR FLYLITE, топовая модель линейки VAPOR 2019 года. Встречайте новый флагман семейства VAPOR 2019 модельного года – клюшку VAPOR FLYLITE! Новая топовая модель является преемником модели VAPOR 1X LITE, но стала лучше ее буквально во всем: стала легче на 10 грамм, на 8% быстрее кистевые броски, на 11% быстрее щелчки. Данная клюшка имеет нижнюю точку прогиба. При производстве данной клюшки используется карбоновое волокно нового поколения NEW TeXtreme, которое позволяет снизить вес клюшки и увеличить ее прочность. Так же для увеличения долговечности и прочности клюшки используется смола DURAFLEX. Новая технология XE TAPER, которая уменьшает вес клюшки, способствует более быстрому схождению шайбы с крюка и уменьшает его проворачиваемость. Длина производителя - 60 дюймов Полная длина клюшки - 166 см Особенности: • Уровень – Элитный • Точка прогиба – Нижняя • Технология броска – XE TAPER • Технология трубы – TeXtreme; DURAFLEX; ACL • Технология крюка – ACL; AERO-SENSE2; PURE SHOT BLADE PROFILE • Конструкция – MONOCOMP • Геометрия шафта – С закругленными краями Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/fb8/400_400_140cd750bba9870f18aada2478b24840a/5301f177_77ab_11e9_aa99_a4bf0158c9f5_744c390d_c9dd_11e9_aa9a_a4bf0158c9f5.jpg?158329639220705', null, 'VAPOR FLYLITE GRIP', 1, 719, 14, 'C:\Users\vinto/images/Клюшки игрока/BAUER_VAPOR FLYLITE GRIP_SR.png');
