INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (1, 'YTH', '', 'Описание Коньки BAUER SUPREME S140 YTH, детская, любительская модель в новой линейке Supreme 2016 года. Внешнее материал ботинка выполнен из нейлона и отличается высокой стойкостью к механическим нагрузкам, истиранию и порезам. Ботинок имеет оптимальный для новичка уровень жесткости и обеспечивает необходимую стабильность и устойчивость. Внутренняя обивка в области голеностопного сустава с интегрированной анатомической пяточной чашей создает необходимую поддержку и плотно фиксирует стопу внутри ботинка. Внутренний язычок анатомической конструкции удобно охватывает переднюю часть голеностопа, обеспечивая необходимую защиту. Внутренняя футеровка язычка выполнена из белого войлока плотностью 30 унций. Внутренняя стелька из формованной, легкой пены EVA способствует удобному положению стопы внутри ботинка, обеспечивает хорошую теплоизоляцию. Внутренняя подкладка из микрофибры отличается высокой износостойкостью и мягкостью. Подошва выполнена из термопластичной резины. На коньках этой модели установлены прочные и легкие стаканы Tuuk Lightspeed Pro. Несменные лезвия Tuuk Super Stainless Steel выполнены из нержавеющей стали. Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a9d/400_400_140cd750bba9870f18aada2478b24840a/0aaf70da_4f19_11e6_b50e_e0cb4ee6cf35_fae64379_51d5_11e6_9c01_3085a97fbce9.jpg?158327132848259', null, 'SUPREME S140', 1, 3, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S140_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (3, 'YTH', '', 'Описание Коньки BAUER VAPOR S17 X300 - это любительская модель в линейке VAPOR 2017 года. Похоже, некоторые игроки просто знают, куда полетит шайба. Такие игроки всегда на один шаг опережают своих соперников — они находятся в эпицентре момента, а не наблюдают со стороны. Если это про вас, проверьте коньки X300 с композитными интегрированными вставками из волокна quarter fit. Они быстрые. Маневренные. И готовы к игре. ПРИЛЕГАНИЕ: • ПОСАДКА КОНЬКОВ VAPOR - Катайтесь еще быстрее в этих коньках со скошенной посадкой. Благодаря стандартной посадке в зоне для пальцев и скошенной посадке типа V в зоне лодыжки и пятки игроки могут ускоряться и поворачивать в этих высококлассных коньках, созданных для скорости и подвижности. ВЕРХНЯЯ ЧАСТЬ: • Конструкция Quarter Package - Композитные интегрированные вставки из волокна quarter fit • Материал подкладки - Микрофибра • Защита лодыжки - Подушки для лодыжки из анатомического пеноматериала. • Конструкция языка - Анатомической формы, из двух частей, из войлока • Стелька - Стандартный комфорт • Термоформуемость - Терморегулируемый верх НИЖНЯЯ ЧАСТЬ: • Стакан (держатель для лезвия) - TUUK LIGHTSPEED Pro • Лезвие - Нержавеющая сталь TUUK Характеристики Возрастная группа YTH Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/855/400_400_140cd750bba9870f18aada2478b24840a/2d6faeb4_0d35_11e7_9c76_3085a97fbce9_827fbbc4_7c04_11e7_9ca5_3085a97fbce9.jpg?158327559253556', null, 'VAPOR S17 X300', 1, 5, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR S17 X300_YTH.png');
