INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (31, 'JR', '', 'Описание Коньки BAUER SUPREME S170, полупрофессиональная модель в новой линейке SUPREME 2016 года. При изготовлении внешнего покрытия ботинка используется легкий материал 3D-lasted Tech Mesh, который обеспечивает высокую прочность, отличную жесткость и необходимую поддержку. Термоформируемый верх позволяет подобрать комфортную, анатомически правильную посадку. Набивка вокруг голеностопного сустава сделана из легкой пены с эффектом памяти, которая аккуратно облегает голеностоп, сохраняя форму и устраняя пустое пространство вокруг суставов. Внешняя пяточная чаша анатомической формы фиксирует пятку и обеспечивает оптимальную поддержку. Внутренний войлочный язычок Form-Fit, плотностью 48 oz, состоит из двух частей. Внутренняя набивка язычка из толстого слоя пены высокой плотности обеспечивает необходимую защиту передней части голеностопного сустава. Внутренняя подкладка выполнена из гидрофобной микрофибры, которая быстро отводит излишки влаги от стопы с последующим быстрым ее испарением, обеспечивая ногам комфортное состояние. Модернизированная стелька Form-Fit+ имеет противоскользящее покрытие, препятствуя проскальзыванию стопы внутри ботинка и способствуя эффективной передаче энергии при движении. Подошва Pro TPU обладает повышенной жесткостью. На коньках стоят стаканы Tuuk LightSpeed Edge, которые снабжены системой быстрой смены лезвий Trigger System. В стаканах используются лезвия из нержавеющей стали Tuuk LightSpeed LS.1. Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c02/400_400_140cd750bba9870f18aada2478b24840a/7c711482_607b_11e7_9c9b_3085a97fbce9_7c711483_607b_11e7_9c9b_3085a97fbce9.jpg?158331119850813', null, 'SUPREME S170', 1, 33, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME S170_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (33, 'JR', '', 'Описание Коньки BAUER NEXUS N2700, полупрофессиональная модель линейки NEXUS 2018 года. Конек имеет классическую посадку. Особенности этого конька в его комфорте. Внешний материал конька изготовлен из высокопрочного полиуретана 3-D Trueform tech PU. Язычок имеет двухкомпонентную конструкцию и выполнен из войлока плотностью 40 унций. Внутренняя подкладка сделана из материала гидрофобной микрофибры, которая отталкивает влагу. Подошва изготовлена из термопластичного полиуретана. Особенности: • Уровень – Любительский • Внешний материал – 3-D TRUEFORM TECH PU • Внутренний материал – HYDROPHOBIC MICROFIBER • Язык – Войлок 40 унций • Стелька – FORM-FIT EVA • Тормоформуемость – Частичная в области лодыжки • Подошва – TPR • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK S.STAINLESS Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f87/400_400_140cd750bba9870f18aada2478b24840a/f31b4278_7a2f_11e8_995d_3085a97fbce9_f31b427e_7a2f_11e8_995d_3085a97fbce9.jpg?158331068663929', null, 'NEXUS N2700', 1, 35, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_NEXUS N2700_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (38, 'SR', '', 'Описание Коньки BAUER SUPREME 170, полупрофессиональная модель в линейке SUPREME 2014 года. Выполнены из современных композитных материалов, которые обеспечивают устойчивость, необходимую жесткость и прочность ботинок, и высокий уровень защиты ноги игрока. Внешнее покрытие корпуса ботинок, изготовленное из облегченных материалов по технологии 3D Ultra Lightweight Tech Mesh, устойчиво к истиранию и механическим повреждениям. Термоформируемый корпус отлично адаптирует ботинок к индивидуальной форме стопы хоккеиста. Для ликвидации пустого пространства в области щиколотки и плотной фиксации пятки используются анатомические вкладыши из пены AnaForm Fit. Внутренний, трехсоставной язычок Form-Fit набивкой из войлока плотностью 48 oz плотно облегает переднюю часть голеностопного сустава и имеет дополнительный элемент защиты голеностопа. Внутренняя стелька выполнена из пены Form Fit EVA, имеет интегрированный элемент поддержки пятки, что обеспечивает удобное и устойчивое положение стопы внутри ботинка. Гидрофобная микрофибра, используемая в качестве внутренне подкладки имеет специальную пропитку, исключающую проскальзывание ноги в ботинках, и обеспечивает эффективной отвод влаги и излишнего тепла. Подошва выполнена из прочной, сублимированной термопластичной резины. Коньки оснащены ударопрочными стаканами Tuuk LightSpeed Edge Holder с завышенной на 3мм передней и задней частью, что обеспечивает максимально резкий разворот. Инновационная система, применяемая в данной модели, позволит в считанные секунды сменить лезвия при необходимости. Лезвия TUUK STAINLESS RUNNER, выполнены из нержавеющей стали. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/77d/400_400_140cd750bba9870f18aada2478b24840a/978f8486_0bd1_11e6_9fde_001fc6b58537_389575df_391e_11