INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (46, 'JR', '', 'Описание Коньки BAUER NEXUS N9000 профессиональная модель в линейке Nexus 2016 года. Затяните шнурки и приготовьтесь взять управление в свои руки благодаря усовершенствованному качеству прилегания коньков и глубокому заднику, который надежно удерживает пятку, обеспечивая непревзойденный контроль и мощь при каждом шаге. А благодаря ультралегкому композитному материалу CURV® и подстежке HYDRA MAX 2 ваши ступни будут надежно зафиксированы и останутся сухими, а последние шаги не уступят по силе первым. Прилегание. • Посадка коньков NEXUS - Хорошее прилегание от пятки до пальцев. Верхняя часть. • Конструкция Quarter Package - Передняя часть из композитного материала Curv® • Материал подкладки - Подкладка из сетчатого материала HYDRA MAX 2 • Защита лодыжки - Легкие, запоминающие форму тела подушки для лодыжек • Конструкция языка - Из 3 частей, вес 48 унций, из войлока, оснащен метатарзальной защитой • Стелька - FORM-FIT+ с хорошим захватом, защитой от влаги и стабилизатором пятки • Термоформуемость - Верхняя часть анатомической формы Нижняя часть. • Подошва - Pro TPU с графическим дизайном • Стакан - TUUK LIGHTSPEED EDGE • Лезвие - Нержавеющая сталь TUUK LS3 (размеры 4-12), нержавеющая сталь TUUK LS2 (размеры 1-3,5) Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/218/400_400_140cd750bba9870f18aada2478b24840a/c0365f0e_4cb9_11e6_88ad_e0cb4ee6cf35_83000e96_4de8_11e6_9c01_3085a97fbce9.jpg?158331234345717', null, 'NEXUS N9000', 1, 48, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_NEXUS N9000_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (50, 'JR', '', 'Описание Коньки BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Основные особенности этого конька легкость и надежность. Благодаря композитному материалу 3-D LASTED CURV®, конек после формовки садиться максимально комфортно. Новый язычок REFLEX имеет трехсоставную конструкцию и защиту плюсневой кости. Войлочное покрытие языка 48 унций. Особенности: • Уровень – Профессиональный • Внешний материал – 3-D LASTED CURV® • Внутренний материал – LOCK-FIT • Язык – REFLEX • Стелька – LOCK-FIT • Термоформуемость – Полная • Подошва – Композит • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS3 (р-р 4-12) TUUK LS2 (р-р 1-3.5) Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/79b/400_400_140cd750bba9870f18aada2478b24840a/68b5a789_6ae3_11e8_995b_3085a97fbce9_36d88b6d_6443_11ea_aa9b_a4bf0158c9f5.jpg?158402108152278', null, 'SUPREME 2S', 1, 52, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 2S_JR.png');
