INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (62, 'JR', '', 'Описание Коньки BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Конек имеет анатомическую форму, благодаря чему идеально сидит на ноге, улучшая передачу энергии при каждом движении. Ключевые изменения коснулись внешнего материала, теперь конек выполнен из нового композитного материала 3-D lasted carbon CURV®, который обладает легким весом, лучше подается формовке и придает ботинку повышенную прочность. Новый термоформуемый язычок REFLEX PRO, имеет трехсоставную конструкцию и защиту плюсневой кости. Войлочное покрытие языка 52 унции. Новое лезвие LS5 Carbon выполнено из нержавеющей стали, имеют углеродистое покрытие. Особенности: • Уровень – Элитный • Внешний материал – 3-D Lasted carbon CURV® • Внутренний материал – LOCK-FIT PRO • Язык – REFLEX PRO • Стелька – SPEED PLATE 2.0 • Термоформуемость – Полная и стелька • Подошва – Сверхлегкий композит, вентилируемая • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS5 CARBON Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/4b6/400_400_140cd750bba9870f18aada2478b24840a/68b5a788_6ae3_11e8_995b_3085a97fbce9_f9168fcf_6442_11ea_aa9b_a4bf0158c9f5.jpg?158403700960276', null, 'SUPREME 2S PRO', 1, 64, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_SUPREME 2S PRO_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (92, 'SR', '', 'Описание Блин с ловушкой BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Блин данной модели имеет новую форму, а блокировочная панель имеет монолитную конструкцию, которая повышает устойчивость блина и помогает вратарю отбивать шайбы в угол. Так же за счет монолитной конструкции блин имеет очень легкий вес и максимальное удобство. Особенности: ✅Уровень – Любитель ✅Манжета – Запатентованная технология FREE-FLEX ✅Ладонь - NASH ✅Лицевая сторона – Пена средней плотности; монолит ✅Подкладка – Влагоотталкивающий нейлон Ловушка линейки VAPOR сгибаются под углом 60 градусов и закрывается за счет смыкания всей ладони. Данная модель оснащена новой манжетой, которая обеспечивает большую амплитуду движения. При изготовлении ловушки использовались новые материалы, чтобы сделать перчатку гибкой и удобной. Помимо этого, была увеличены глубина кармана. Особенности: ✅Уровень – Любительский ✅Ключевые изменения – Новый глубокий карман; Новый ремень FAST STRAP; Манжета FREE-FLEX ✅Манжета – Монолит ✅Подкладка – NASH ✅Карман – Т-образный ✅Окантовка – Пена средней плотности Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/474/400_400_140cd750bba9870f18aada2478b24840a/51e932bc_d09f_11e9_aa9a_a4bf0158c9f5_17bcac8e_d13e_11e9_aa9a_a4bf0158c9f5.jpg?158331117149042', null, 'VAPOR X2.7  FRT', 1, 94, 24, 'C:\Users\vinto/images/Блин и ловушка/BAUER_VAPOR X2.7  FRT_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (196, 'YTH', '', 'Описание Защита коленей вратаря CCM 1.5 YTH Наколенники создают дополнительный уровень защиты коленного сустава. Высокопрочный нейлон используется в качестве материала внешней отделки наколенников. Легкая пена, формованная для достижения удобной посадки, используется для внутреннего наполнения и обеспечивает удобную посадку. Регулируемая система крепления имеет одни эластичный ремень шириной 5 см для фиксации на бедре и два эластичных ремня шириной 3,8 см для фиксации под коленом и ниже колена. Данный размер подходит на возраст от 5 до 9 лет Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/841/be308e2a_9a03_11e7_9cb4_3085a97fbce9_c8f88e9c_a2ef_11e7_9cb9_3085a97fbce9.jpg?1557611577258468', null, '1.5', 2, 198, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_1.5_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (67, 'SR', '', 'Описание Коньки CCM SUPER TACKS AS1 Особенности: Внешний слой: MONOFRAME 360 – CARBON COMPOSITE – Ботинок изготовлен из нового премиального композитного материала, имеет цельную конструкцию, как у своего предшественника Super Tacks. Средний слой: SPEEDCORE STIFFNESS 3 - Внутреннее ядро конька обеспечивает повышенную жесткость, способствует снижению веса, сохраняя при этом высокий уровень комфорта. Внутренний материал: TOTALDRI PRO W/ DURAZONE ABRASION RESISTANCE - внутренняя подкладка с абразивным покрытием, превосходно справляется с выведением влаги, фиксирует ногу внутри ботинка, препятствуя ее смещению, и обладает высокой износостойкостью. Комфортные вставки: NEW SMOOTH CONTOUR PRO – дополнительные подушки, защищают ногу от повреждений о край ботинка. Язычок: TRITECH FLEX – обновленный язык, в составе которого вместо пластиковой вставки, используется пена высокой плотности толщиной 7 мм, что позволило уменьшить вес, улучшить подвижность, сохранив все защитные свойства. Стелька: CCM ORTHOMOVE - стелька может быть использована индивидуально для стоп разной пронации, благодаря специальным вставкам. Жесткая основа PowerGrid в стельке, придаёт дополнительную жесткость пятки, что максимизирует передачу энергии и обеспечивает лучшую стабильность Стакан: SPEEDBLADE 4.0 - уменьшает угол атаки. Лезвие: SPEEDBLADE BLACK – из нержавеющей стали с черным покрытием. Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e4b/400_400_140cd750bba9870f18aada2478b24840a/4f378997_6000_11e8_9958_3085a97fbce9_61732cf6_66a2_11e8_9959_3085a97fbce9.jpg?158329124558898', null, 'SUPER TACKS AS1', 2, 69, 7, 'C:\Users\vinto/images/Коньки хоккейные/CCM_SUPER TACKS AS1_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (75, 'SR', '', 'Описание Коньки BAUER NEXUS 2N, топовая модель линейки NEXUS 2018 года. Конек имеет классическую посадку. Особенности этого конька в его комфорте и долговечности. Внешний материал конька изготовлен из композита 3-D LASTED CURV, который делает конек очень легким, но при этом очень надежным. Язычок FORM-FIT 2 имеет двухкомпонентную конструкцию и выполнен из войлока плотностью 48 унций. Внутренняя подкладка сделана из материала HYDRA MAX2, которая отлично впитывает влагу. Особенности: • Уровень – Профессиональный • Внешний материал – 3-D LASTED CURV® • Внутренний материал – HYDRA MAX2 • Язык – FORM-FIT2 • Стелька – FORM-FIT+ • Тормоформуемость – Полная • Подошва – Композит • Стакан – TUUK LIGHTSPEED EDGE • Лезвие – TUUK LS3 (р-р 4-12 ) TUUK LS2 (р-р 3-3.5 ) Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d20/400_400_140cd750bba9870f18aada2478b24840a/515d1538_573e_11e8_9958_3085a97fbce9_885a84ac_6fa4_11e8_995c_3085a97fbce9.jpg?158330666968211', null, 'NEXUS 2N', 1, 77, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_NEXUS 2N_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (78, 'SR', '', 'Описание Коньки BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. При создании коньков использовалась система DYNAFLEX, эта система обеспечивает игроку максимальную производительность при каждом движении. Ботинок выполнен из композитного материала 3D LASTED CURV, который обладает очень легким весом и хорошими защитными функциями. Данная модель имеет ассиметричный носок, что придает коньку максимальное удобство. Благодаря новой композитной шнуровки ботинок лучше прилегает к стопе, поддерживает ее и позволяет игроку лучше контролировать свое катание. Зона лодыжки выполнена из пены с эффектом памяти AERO FOAM PRO, которая обладает улучшенными свойствами во время формовки. В качестве внутренней подкладки используется материал LOCK-FIT PRO, которая эффективно отводит влагу. Данный ботинок ассиметричной клиновидной формы лучше поддерживает стопу во время катания. Особенности: ✅Уровень – Элитный ✅Внешний материал – 3D LASTED CURV ✅Внутренний материал – AERO FOAM PRO; LOCK-FIT PRO ✅Язык – RECOIL PRO TONGUE ✅Стелька – DYNAMIC FLEX ✅Термоформуемость – Полная ✅Подошва – Композит ✅Стакан – TUUK LIGHTSPEED EDGE ✅Лезвие – TUUK LS5 Внимание: На данный товар максимальная скидка 10% Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/57c/400_400_140cd750bba9870f18aada2478b24840a/e94ea172_762d_11e9_aa99_a4bf0158c9f5_1c28ffbb_99ab_11e9_aa9a_a4bf0158c9f5.jpg?158327701861165', null, 'VAPOR 2X PRO', 1, 80, 7, 'C:\Users\vinto/images/Коньки хоккейные/BAUER_VAPOR 2X PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (86, 'YTH', '', 'Описание Блин с ловушкой CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Блин с ловушкой подходят для начинающих вратарей. Данная модель очень мобильна и удобна. Внешний материал комплекта SPEEDSKIN, который обеспечивает долговечность и ощущение плотного прилегания. Так же блин с ловушкой имеют очень легкий вес. Ловушка имеет невероятную гибкость, благодаря этому вратарь очень легко сможет ее сомкнуть. Конструкция ловушки состоит из двух частей для большей гибкости в области запястья. Угол закрытия ловушки – 60 градусов, придает вратарю более легкое закрытие и обеспечивает комфорт во время использования. Конструкция блина позволяет вратарю точно и быстро перенаправить шайбу в углы. Блокировочная панель выполнена из пены низкой плотности и пластика, благодаря этому блин имеет легкий вес. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: ✅Возраст – Дети (4-7 лет) ✅Материал – SPEEDSKIN ✅Конструкция блина – Цельная ✅Ладонь - NASH ✅Конструкция ловушки – Двухсоставная, для большей гибкости. ✅Угол закрытия – 60 градусов Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f66/400_400_140cd750bba9870f18aada2478b24840a/c30fce87_8ea3_11e9_aa99_a4bf0158c9f5_3b54a9db_91c4_11e9_aa9a_a4bf0158c9f5.jpg?158330651531971', null, 'YFLEX 2', 2, 88, 24, 'C:\Users\vinto/images/Блин и ловушка/CCM_YFLEX 2_YTH.png');
