INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (110, 'SR', '', 'Описание Коньки вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Коньки оснащены стаканами VERTEXX EDGE и лезвиями LS 1G, которые обеспечивают вратарю больший угол атаки. Носок данной модели усилен, что делает их более защищенными. Внешний материал ботинка изготовлен из материала TEXPLATE , за счет чего они более жесткие и устойчивые, но не такие жесткие как топовые коньки. У коньков ассиметричная форма ботинка. Подошва выполнена из облегченной композитной конструкции TPR. Особенности: • Уровень – Любительский • Внешний материал – TEXPLATE • Язык – Ассиметричный войлочный язык • Подошва – TPR • Стакан – VERTEXX EDGE • Лезвие – LS 1G • Петля LUNDQVUST – Есть • Подкладка – Влагоотталкивающий материал Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ce9/400_400_140cd750bba9870f18aada2478b24840a/eea53e82_93c1_11e8_995e_3085a97fbce9_eea53e83_93c1_11e8_995e_3085a97fbce9.jpg?158331050251254', null, 'SUPREME S27', 1, 112, 21, 'C:\Users\vinto/images/Вратарские коньки/BAUER_SUPREME S27_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (116, 'SR', '', 'Описание Коньки вратаря BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Коньки оснащены стаканами VERTEXX EDGE и лезвиями LS 5G CARBON, что улучшает устойчивость на ребрах коньков и угол атаки, поскольку теперь лезвие на 4мм выше, чем остальные. Носок данной модели усилен, что делает их более защищенными. Внешний материал ботинка изготовлен из композитного материала CURV ,который обеспечивает оптимальную посадку после формовки. Внутренняя подкладка LOCK-FIT PRO, предотвращает соскальзывание в области пятки за счет микро зубчиков, которые цепляются за носок. У коньков ассиметричная форма ботинка, обеспечивающая вратарю удобство в области пятки, когда он находится в стойке. Особенности: • Уровень – Топовый • Внешний материал – CURV • Язык – Ассиметричный • Подошва – Композитная; вентилируемая • Стакан – VERTEXX EDGE • Лезвие – LS 5G • Петля LUNDQVUST – Есть • Подкладка – LOCK-FIT PRO Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8d3/400_400_140cd750bba9870f18aada2478b24840a/515d1535_573e_11e8_9958_3085a97fbce9_cda82418_580d_11e8_9958_3085a97fbce9.jpg?158331080549285', null, 'SUPREME 2S PRO', 1, 118, 21, 'C:\Users\vinto/images/Вратарские коньки/BAUER_SUPREME 2S PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (117, 'SR', '', 'Описание Коньки вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. В начале бы хотелось отметить, что все коньки линейки VAPOR имеют увеличенный объем для более удобной посадки. Данный конек выполнен из композитного материала CURV®, который отличается малым весом и при этом имеет отличные защитные свойства. Коньки оснащены язычком DINAMIC FLEX, который делает верхнюю часть ботинка более гибкой. В качестве внутренней подкладки используется материал LOCK FIT PRO, который отличается нескользящей поверхностью и фиксирует стопу в ключевых зонах. В данной модели используется лезвие LS5 с карбоновым покрытием, благодаря которому лезвие дольше держит заточку. Особенности: ✅Уровень – Топовый ✅Внешний материал – CURV ✅Язык – DINAMIC FLEX ✅Подошва – Композитная; вентилируемая ✅Стакан – VERTEXX EDGE ✅Лезвие – LS 5G ✅Петля LUNDQVUST – Есть ✅Подкладка – LOCK-FIT PRO Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3f6/400_400_140cd750bba9870f18aada2478b24840a/b98eddf7_77ab_11e9_aa99_a4bf0158c9f5_9118da89_7b43_11e9_aa99_a4bf0158c9f5.jpg?158327831152433', null, 'VAPOR 2X PRO', 1, 119, 21, 'C:\Users\vinto/images/Вратарские коньки/BAUER_VAPOR 2X PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (123, 'SR', '', 'Описание Щитки BAUER VAPOR X2.7, любительская модель линейки VAPOR 2019 года. Данные модель имеет конструкцию DINAMIC FLEX и ботинок с изгибом на 100 градусов, благодаря этому повышается гибкость щитка и улучшается посадка в области стопы. Так же щиток оснащен системой ремней TUNE FIT, благодаря которой его можно максимально отрегулировать по ноге, что придает максимальное удобство. На носке щитка есть крепление MONSTER H.A.L., которое представляет собой эластичный жгут, обеспечивающий плотное прилегание стопы вратаря ко льду, за счет чего улучшается угол атаки. Особенности: ✅Уровень – Любительский ✅Конструкция –Мягкая, с двумя изломами под коленом и над коленом ✅Материал – Модернизированный синтетический ✅Ремни – TUNE FIT ✅Голень – Нейлон Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c61/400_400_140cd750bba9870f18aada2478b24840a/50314c28_927f_11e9_aa9a_a4bf0158c9f5_beb4643e_9329_11e9_aa9a_a4bf0158c9f5.jpg?158328704533934', null, 'VAPOR X2.7', 1, 125, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_VAPOR X2.7_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (124, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX E4.5, модель линейки EXTREME FLEX 2019 года. Данная модель отличается хорошим уровнем защиты, гибкостью и производительностью. Внешний материал щитков выполнен из синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Двойная легкая сердцевина создает ощущение гибкости под коленом и жесткости над ним, обеспечивая устойчивость и защиту. На обратной стороне вы получаете систему ремешков, обеспечивающую быстроту движений. Она будет удерживать ногу и придавать щитку плотное прилегание. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. Особенности: ✅Уровень – Любительский ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6f1/400_400_140cd750bba9870f18aada2478b24840a/3ab527e1_ebef_11e9_aa9a_a4bf0158c9f5_6bfe71a6_f1a8_11e9_aa9a_a4bf0158c9f5.jpg?158331092054589', null, 'EXTREME FLEX E4.5', 2, 126, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_EXTREME FLEX E4.5_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (127, 'SR', '', 'Описание Щитки BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Щитки имеют более жесткую конструкцию с одним изломом под коленом, чем предыдущая коллекция, благодаря чему повысилась прочность и долговечность. Так же щитки имеют систему регулировки CRS для лучшего удобства. Пластины в области колена и голени выполнены из материала C.O.R.T.E.C.H. для увеличения скольжения. Крепление на носке смещено, что обеспечивает вратарю улучшенный угол атаки. Внутренняя подкладка из влагоотталкивающего нейлона. Особенности: • Уровень – Полупрофессиональный • Конструкция – Жесткая, с одним изломом под коленом • Материал – Искусственная кожа; C.O.R.T.E.C.H. в области колена и голени • Ремни – CRS • Колено – Конструкция AKL • Голень – Пена VENT ARMOR Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/776/400_400_140cd750bba9870f18aada2478b24840a/f9b9eaba_85d5_11e8_995e_3085a97fbce9_f9b9eac2_85d5_11e8_995e_3085a97fbce9.jpg?158331107449485', null, 'SUPREME S29', 1, 129, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_SUPREME S29_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (128, 'SR', '', 'Описание Щитки вратаря CCM EXTREME FLEX E4.9, профессиональная модель линейки EXTREME FLEX 2019 года. Данная модель отличается высоким уровнем защиты, максимальной гибкостью и производительностью. Внешний материал щитков выполнен из высокопрочного синтетического материала SPEEDSKIN. Этот материал обеспечивает максимально быстрое скольжения во время игры. Двойная легкая сердцевина создает ощущение гибкости под коленом и жесткости над ним, обеспечивая устойчивость и защиту. На обратной стороне вы получаете систему ремешков, обеспечивающую быстроту движений. Она будет удерживать ногу и придавать щитку плотное прилегание. Наколенник, выполнен по технологии трехмерного литья и обеспечивает эффективную амортизацию и дополнительное удобство. Особенности: ✅Уровень – Профессиональный ✅Конструкция – Мягкая, с двумя изломами под коленом и над коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/5e6/400_400_140cd750bba9870f18aada2478b24840a/7c8df455_8ea2_11e9_aa99_a4bf0158c9f5_facaf326_9469_11e9_aa9a_a4bf0158c9f5.jpg?158328090437315', null, 'EXTREME FLEX E4.9', 2, 130, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_EXTREME FLEX E4.9_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (131, 'SR', '', 'Описание Щитки вратаря CCM PREMIER II PRO SR, топовая модель линейки PREMIER 2018 года. Внешний материал щитков SPEEDSKIN, который лучше скользит по льду. Внутренняя конструкция щитков выполнена по новой технологии, благодаря этому они весят на 20% меньше, чем предыдущая коллекция. В основу этих щитков положена технология MAX REBOUND (максимальный отскок). По сути это вспененный материал, при попадании в который шайба отскакивает далеко в поле. Так же в этих щитках изменена нижняя часть, закрывающая конек, теперь вратарю будет проще отталкиваться ото льда, стоя на коленях. С обратной стороны щитки оснащены эластичными ремешками STRAP SYSTEM, благодаря чему вратарю проще садиться в бабочку и вставать на ноги. Особенности: ✅Уровень – Элитный ✅Конструкция – Жесткая, с одним изломом под коленом ✅Материал – SPEEDSKIN Характеристики Возрастная группа SR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/9d5/400_400_140cd750bba9870f18aada2478b24840a/0fe7f880_9a0b_11e8_995f_3085a97fbce9_0fe7f881_9a0b_11e8_995f_3085a97fbce9.jpg?158331101168444', null, 'PREMIER II PRO', 2, 133, 22, 'C:\Users\vinto/images/Вратарские щитки/CCM_PREMIER II PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (133, 'SR', '', 'Описание Щитки BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. Внешняя часть щитков выполнена из материала C.O.R.T.E.C.H., благодаря ему улучшено скольжение, повышена прочность. Если сравнивать с 1S, то щитки стали более гибкими. Для повышения прочности щитков в качестве их основы использован материал POWERLITE, этот материал представляет собой смесь пены EPP и композитного материала CURV. Щитки имеют более жесткую конструкцию с одним изломом под коленом, чем предыдущая коллекция, благодаря чему повысилась прочность и долговечность. Так же щитки имеют систему регулировки CRS для лучшего удобства. Особенности: • Уровень – Элитный • Конструкция – Жесткая, с одним изломом под коленом • Материал –C.O.R.T.E.C.H. • Ремни – CRS • Колено – Конструкция NASH • Голень – Пена VENT ARMOR; 37,5 Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/66c/400_400_140cd750bba9870f18aada2478b24840a/9309c51b_8414_11e8_995d_3085a97fbce9_17c72329_84ec_11e8_995e_3085a97fbce9.jpg?158327601744793', null, 'SUPREME 2S PRO', 1, 135, 22, 'C:\Users\vinto/images/Вратарские щитки/BAUER_SUPREME 2S PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (138, 'SR', '', 'Описание Шлем вратаря BAUER NME VTX, топовая модель линейки NME 2018 года. Внешнее покрытие шлема выполнено из углеродного волокна TeXtreme, которое обладает отличными защитными свойствами и имеет очень легкий вес. Внутренняя подкладка пена PORON XRD, которая поглощает энергию и технология VTX. Подбородник так же выполнен из пены PORON XRD. Подкладка на внутреннем вкладыше в области лба с технологией 37,5 препятствует попаданию влаги в глаза и быстро сохнет. Особенности: • Уровень – Элитный • Внешнее покрытие – TeXtreme • Ключевые технологии – VTX; XRD PORON; TeXtreme; THERMOFORMED IX Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/472/400_400_140cd750bba9870f18aada2478b24840a/68b5a790_6ae3_11e8_995b_3085a97fbce9_885a84c5_6fa4_11e8_995c_3085a97fbce9.jpg?158331028157215', null, 'NME VTX', 1, 140, 23, 'C:\Users\vinto/images/Вратарские шлема/BAUER_NME VTX_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (139, 'SR', '', 'Описание Шлем вратаря BAUER PROFILE 960XPM, топовая модель линейки PROFILE . Один из самых лучших вратарских шлемов и самый популярный шлем в NHL. Внешняя оболочка шлема выполнена из стеклопластика плотностью 12К и дополнена усиленной амортизирующей лентой 3М для максимального смягчения ударов шайбой. В качестве внутренней пены используется PORON XRD, которая обеспечивает максимальный комфорт и рассеивает энергию. Подбородник изготовлен из материала PORON XRD Особенности: • Уровень – Топовый • Внешнее покрытие – Карбон 12K; INNEGRA • Ключевые технологии – VN FOAM; PORON XRD • Внутренняя подкладка – 37,5 Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b2d/400_400_140cd750bba9870f18aada2478b24840a/6edc06d4_48bc_11e6_b11e_a9e9ddf2845b_4281516e_52a5_11e6_9c01_3085a97fbce9.jpg?158331118547952', null, 'PROFILE 960XPM', 1, 141, 23, 'C:\Users\vinto/images/Вратарские шлема/BAUER_PROFILE 960XPM_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (148, 'JR', '', 'Описание Трусы вратаря BAUER SUPREME S27, любительская модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Любительский • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – MD FOAM • Защита спины – Пена • Подкладка – Влагоотталкивающая Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/eef/400_400_140cd750bba9870f18aada2478b24840a/b9535a66_865d_11e8_995e_3085a97fbce9_b9535a67_865d_11e8_995e_3085a97fbce9.jpg?158330773541069', null, 'SUPREME S27', 1, 150, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_SUPREME S27_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (152, 'JR', '', 'Описание Трусы вратаря CCM EXTREME FLEX SHIELD E2.5, юниорская профессиональная модель линейки EXTREME FLEX 2018 года. Данная модель имеет такую же конструкцию, как линейка PREMIER в области талии и ниже. Трусы линейки EXTREME FLEX более плотней прилегают к талии и сидят как влитые, что намного лучше с точки зрения комфорта. С изнанки предусмотрена специальная система, благодаря которой шорты сидят очень плотно на бедрах. В области тазобедренных суставах используется сегментированная пена высокой плотности. Для максимальной защиты в области бедра используется пластик с вставками из полиэтилена. Главное отличие от PREMIER заключается в том, как сидят трусы в области талии. Так же трусы имеют систему регулировки, благодаря которой трусы можно опустить на 1 дюйм (2,5СМ). Чехол в данной модели выполнены из нейлона плотность 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; полиэтилен ✅Защита тазобедренная – Пена высокой плотности ✅Защита позвоночника – Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); внутренние ремни по объему ✅Чехол –Нейлон 400 ден Характеристики Возрастная группа JR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/bdb/400_400_140cd750bba9870f18aada2478b24840a/f3002060_af6f_11e8_9963_3085a97fbce9_f3002061_af6f_11e8_9963_3085a97fbce9.jpg?158331522841949', null, 'EXTREME FLEX SHIELD E2.5', 2, 154, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_EXTREME FLEX SHIELD E2.5_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (154, 'JR', '', 'Описание Трусы вратаря CCM PREMIER R1.5 LE, юниорская профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Все защитные элементы выполнены из высококачественного полиэтилена. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа JR Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/d5f/400_400_140cd750bba9870f18aada2478b24840a/bbd04eff_8ea1_11e9_aa99_a4bf0158c9f5_b7dac0d1_9267_11e9_aa9a_a4bf0158c9f5.jpg?158331058542025', null, 'PREMIER R1.5 LE', 2, 156, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_PREMIER R1.5 LE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (158, 'INT', '', 'Описание Трусы вратаря CCM PREMIER R1.9 LE, профессиональная модель линейки PREMIER 2019 года. Данная модель обеспечивает свободную посадку на талии. В трусах присутствует система внутреннего пояса, с помощью которой можно отрегулировать плотность прилегание трусов к талии, что позволяет максимально увеличить зону защиты ворот. Набедренные щитки квадратные и широкие, чтобы обеспечить обхват зоны перед воротами. Так же в данной модели присутствует система регулировки по длине на 1 дюйм (2,5см). Все защитные элементы выполнены из высококачественного полиэтилена. Область позвоночника выполнена из материала EVA. Чехол трусов выполнен из нейлона плотностью 400 ден с эластичными вставками для большего комфорта. Особенности: ✅Уровень – Профессиональный ✅Защита бедра – Пластик; Полиэтилен ✅Защита паха – Полиэтилен ✅Защита позвоночника - EVA ✅Регулировка – По длине на 1 дюйм (2,5 см); Внутренние ремни по объему ✅Чехол – Нейлон 400 ден Характеристики Возрастная группа INT Линейка PREMIER', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/68c/400_400_140cd750bba9870f18aada2478b24840a/9f5ee87f_8ea1_11e9_aa99_a4bf0158c9f5_c137530c_9267_11e9_aa9a_a4bf0158c9f5.jpg?158331364840069', null, 'PREMIER R1.9 LE', 2, 160, 20, 'C:\Users\vinto/images/Вратарские трусы/CCM_PREMIER R1.9 LE_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (159, 'SR', '', 'Описание Трусы вратаря BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2018 года. Трусы оснащены щитками на бедрах и соответствуют новым размерам NHL, ширина трусов стала 9 дюймов, а не как раньше было 10. Трусы имеют анатомическую посадку в области талии с новой системой ремней фиксации для надежной посадки на поясе. Защита тазобедренных суставов имеет плавающую конструкцию и выполнена с использованием пены PORON XRD, которая эффективно поглощает энергию удара, создавая отличную защиту. Набедренные щитки обладают отличными защитными свойствами, точно повторяют анатомические контуры тела, обеспечивая комфортную посадку и плотное прилегание элементов защиты. Внутренняя подкладка выполнена из материала THERMO MAX. Так же эти трусы можно увеличить на 1 дюйм(2,5см). Особенности: • Уровень – Полупрофессиональный • Регулировка – Система удлинения на 1 дюйм(2,5см) • Защита бедра – PORON XRD • Защита спины – Пена • Подкладка – THERMO MAX Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/551/400_400_140cd750bba9870f18aada2478b24840a/4d4acd7f_d600_11e8_9984_3085a97fbce9_154e192a_d74f_11e8_9984_3085a97fbce9.jpg?158331172141556', null, 'SUPREME S29', 1, 161, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_SUPREME S29_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (165, 'SR', '', 'Описание Трусы вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Трусы отличаются традиционной для серии VAPOR посадкой, прямые штанины и расширение в области бедер. Данная модель дополнена внутренним поясом регулировки, который можно использовать внутри и снаружи трусов. Также трусы оснащены системой EXTENSION FLEX, которая представляет собой застежку-молнию, посредством которой шорты можно удлинить на 1 дюйм(2.5см). Благодаря двухточечному гибкому креплению, повышена подвижность в области бедра. В области бедра используется композитный материал CURV, обеспечивающий повышенную прочность и надежную защиту. Особенности: ✅Уровень – Элитный ✅Регулировка – Система удлинения на 1 дюйм (2.5 см) ✅Защита бедра- CURV ✅Защита спины- FREE FLEX; AEROLITE ✅Подкладка – THERMOCORE Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/cc7/400_400_140cd750bba9870f18aada2478b24840a/c77a10cc_8db9_11e9_aa99_a4bf0158c9f5_34d0ccef_9267_11e9_aa9a_a4bf0158c9f5.jpg?158331507247832', null, 'VAPOR 2X PRO', 1, 167, 20, 'C:\Users\vinto/images/Вратарские трусы/BAUER_VAPOR 2X PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (170, 'YTH', '', 'Описание Наплечник вратаря CCM YFLEX 2 YTH, детская модель линейки YFLEX 2019 года. Конструкция защиты наплечника была полностью изменена, теперь он больше охватывает тело, больше подвижен, чем предыдущая коллекция. Так же появилась возможность регулировки в области плеч для более оптимальной посадки. Все защитные элементы наплечника выполнены из полиэтилена, благодаря этому наплечник имеет хорошие защитные свойства и легкий вес. Особенности: ✅Уровень – Детский, профессиональный ✅Защита плеч – Полиэтилен ✅Защита рук – Полиэтилен ✅Защита груди – Полиэтилен ✅Защита ребер – Полиэтилен Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/962/400_400_140cd750bba9870f18aada2478b24840a/66aa6c17_8ea3_11e9_aa99_a4bf0158c9f5_7729f554_9262_11e9_aa9a_a4bf0158c9f5.jpg?158330968353906', null, 'YFLEX 2', 2, 172, 19, 'C:\Users\vinto/images/Вратаские нагрудники/CCM_YFLEX 2_YTH.png');
