INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (185, 'SR', '', 'Описание Наплечник вратаря BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2018 года. За счет уменьшения плечевых чашек, наплечник получился очень мобильным. В области плеча использована пена POWERLITE, который представляет собой смесь вспененного пропилена, пены высокой плотности и композитного материала CURV®, обеспечивающий защитные свойства. Защита локтя выполнена из пены PORON XRD для лучшего поглощения удара. Так же эта пена использована во всей области защиты рук. Внутренняя подкладка 37,5®, которая обработана по технологии SANITIZED®, для предотвращения развития бактерий и возникновения неприятного запаха. Особенности: • Уровень – Элитный • Регулировка – Два эластичных ремня в области спины • Защита плеч – POWERLITE • Защита рук – PORON XRD; POWERLITE • Защита груди – VENT ARMOR • Защита ребер – Пена высокой плотности • Подкладка – 37,5®; SANITIZED® Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6e4/400_400_140cd750bba9870f18aada2478b24840a/b9535a4b_865d_11e8_995e_3085a97fbce9_b9535a4c_865d_11e8_995e_3085a97fbce9.jpg?158331235049938', null, 'SUPREME 2S PRO', 1, 187, 19, 'C:\Users\vinto/images/Вратаские нагрудники/BAUER_SUPREME 2S PRO_SR.png');
