INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (212, 'JR', '', 'Описание Защита коленей вратаря CCM 1.5 JR Наколенники создают дополнительный уровень защиты коленного сустава. Высокопрочный нейлон используется в качестве материала внешней отделки наколенников. Легкая пена, формованная для достижения удобной посадки, используется для внутреннего наполнения и обеспечивает удобную посадку. Регулируемая система крепления имеет одни эластичный ремень шириной 5 см для фиксации на бедре и два эластичных ремня шириной 3,8 см для фиксации под коленом и ниже колена. Данный размер подходит на возраст от 9 до 14 лет Характеристики Возрастная группа JR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/35e/be308e28_9a03_11e7_9cb4_3085a97fbce9_c8f88e9b_a2ef_11e7_9cb9_3085a97fbce9.jpg?1557613328258468', null, '1.5', 2, 214, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_1.5_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (221, 'SR', '', 'Описание Защита коленей вратаря CCM 1.9 Наколенники имеют подвижную, двухкомпонентную конструкцию, которая не ограничивает движений вратаря. Внешняя отделка выполнена из высокопрочного нейлона, устойчивого к истиранию и механическим нагрузкам. Конструкция коленной чаши имеет глубокое посадочное место, что обеспечивает дополнительную поддержку колена. Формованная, коленная чаша выполнена из ударопрочного пластика, обеспечивая высокий уровень защиты. Внутренняя часть защиты колена выполнена из пены средней плотности, которая обеспечивает отличную защиту и имеет легкий вес. Данный размер подходит примерно на возраст от 14 лет Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/iblock/0f7/b17cf52d_4135_11e7_9c8f_3085a97fbce9_b17cf530_4135_11e7_9c8f_3085a97fbce9.jpg?1557613911248040', null, '1.9', 2, 223, 25, 'C:\Users\vinto/images/Аксессуары для вратарей/CCM_1.9_SR.png');
