INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (243, 'SR', '', 'Описание Вратарская клюшка WARRIOR SWAGGER PRO2, профессиональная модель линейки SWAGGER 2018 года. Данная модель является одной из самых прочных клюшек на рынке. Рукоятка выполнена из березового и осинового сердечника, который ламинирован стекловолокном для достижения прочности и долговечности. Крюк имеет вставку из ABS-пластика в области пятки, которая увеличивает его надежность. Улучшенное покрытие в рукоятке и крюке уменьшает образование сколов на поверхности клюшки. Особенности: • Облегченная конструкция • Клюшка с жестким пеноматериалом • Сбалансированная клюшка с применением RIM уретана • Прочный профессиональный DELUXE крюк с амортизирующими вставками ABS • Шафт береза/осина с ламинацией стекловолокном • Сделано в Финляндии Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/723/400_400_140cd750bba9870f18aada2478b24840a/4f4e9343_ba39_11e8_9966_3085a97fbce9_4f4e9344_ba39_11e8_9966_3085a97fbce9.jpg?158327952719926', null, 'SWAGGER PRO 2', 3, 245, 15, 'C:\Users\vinto/images/Вратарские клюшки/WARRIOR_SWAGGER PRO 2_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (252, 'INT', '', 'Описание Клюшка вратаря BAUER VAPOR 2X PRO, топовая модель линейки VAPOR 2019 года. Данная клюшка представлена с крюком TECH STREAM, внутренняя часть которого выполнена из материала AERO FOAM 3 благодаря чему эта клюшка лучше поглощает удары и отличается меньшим весом в сравнении с предыдущей моделью этого поколения 1X. В расширенной части используется материал ADVANCED CARBON LAYERING и проходит воздушный канал, поэтому клюшка стала более легкой, при этом не потеряв своей прочности. А материал TeXtreme® в черенке клюшки обеспечивает превосходную передачу тактильных ощущений. Особенности: • Уровень – Элитный • Шафт – Композит • Крюк – TeXtreme®; AERO FOAM 3 • Расширенная часть – ACL Характеристики Возрастная группа INT Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a2f/400_400_140cd750bba9870f18aada2478b24840a/7be99195_8ce6_11e9_aa99_a4bf0158c9f5_aae88374_928a_11e9_aa9a_a4bf0158c9f5.jpg?158330989819978', null, 'VAPOR 2X PRO', 1, 254, 15, 'C:\Users\vinto/images/Вратарские клюшки/BAUER_VAPOR 2X PRO_INT.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (332, 'JR', '', 'Описание Наплечник BAUER NSX, любительская модель линейки NSX 2018 года. Наплечник предназначен для игры на любительском уровне. Основные защитные элементы выполнены из пены средней плотности. Защита спины состоит из многосегментной пены с добавлением ударопрочного пластика. Особенности: • Уровень – Любительский • Защита плеч – Пена средней плотности MD • Защита груди - Пена средней плотности MD • Передняя защита ребер – Пена средней плотности MD • Защита спины – Пена средней плотности MD • Защита позвоночника – Пена средней плотности MD; ударопрочный пластик • Защита бицепса – Пена средней плотностью MD • Подкладка – HYDROPHOBIC Характеристики Возрастная группа JR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/867/400_400_140cd750bba9870f18aada2478b24840a/6afdb0c8_8666_11e8_995e_3085a97fbce9_6afdb0c9_8666_11e8_995e_3085a97fbce9.jpg?158331037652396', null, 'NSX', 1, 334, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_NSX_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (254, 'SR', '', 'Описание Клюшка вратаря CCM EXTREME FLEX 4, топовая модель линейки EXTREME FLEX 2019 года. Широкая часть клюшки выполнена по технологии INNEGRA, благодаря этому клюшка имеет большую гибкость и хорошо поглощает вибрацию. Технология ZEROFLUX использована в широкой части, так же поглощает вибрацию и контролирует отскок шайбы. В черенке и крюке клюшки использован материал SIGMATEX, благодаря этому клюшка стала прочнее и легче. Так же черенок имеет двойную геометрию, а скругленные углы в клюшке повышают ее прочность и долговечность. Особенности: ✅Уровень – Элитный ✅ Шафт – SIGMATEX; Зона контроля GRIP; ✅Крюк – SIGMATEX ✅Расширенная часть – INNEGRA; ZEROFLUX Характеристики Возрастная группа SR Линейка EXTREME FLEX', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/2f3/400_400_140cd750bba9870f18aada2478b24840a/97141630_b438_11e9_aa9a_a4bf0158c9f5_6100b3b9_b9e0_11e9_aa9a_a4bf0158c9f5.jpg?158331123720605', null, 'EXTREME FLEX 4', 2, 256, 15, 'C:\Users\vinto/images/Вратарские клюшки/CCM_EXTREME FLEX 4_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (258, 'YTH', '', 'Описание Налокотники EASTON SYNERGY GX SOFT YTH детская модель разрабатывалась специально для детей с учетом особенностей строения детского организма и с использованием фирменных технологий, адаптированных для детской хоккейной амуниции. Налокотники имеют односоставную конструкцию, при этом обладают хорошей мобильностью, удобной и плотной посадкой и отличаются хорошими защитными качествами. Элемент защиты бицепса выполнен из пены средней плотности, дополнительные вставки из облегченной, модернизированной пены E-Cell HD способствуют усилению защитных функций, снижению веса налокотников и обеспечению хорошей воздухопроницаемости защиты. Элемент защиты предплечья выполнен из пены средней плотности и эффективно защищает руки игрока от ударов по касательной. Подкладка, выполненная из эластичного материала, способствует быстрому поглощению влаги и созданию хорошей циркуляции воздуха внутри, а так же обеспечивает эффективную терморегуляцию, отводя излишки тепла от тела. Анатомическая посадка и надежная фиксация налокотников с помощью двух ремешков крепления исключает их смещение при активном движении. Характеристики Возрастная группа YTH Линейка SYNERGY', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/989/400_400_140cd750bba9870f18aada2478b24840a/f56ff9f4_8baf_11e6_9c14_3085a97fbce9_04b84846_968a_11e6_9c17_3085a97fbce9.jpeg?158328826333742', null, 'SYNERGY GX SOFT', 12, 260, 8, 'C:\Users\vinto/images/Налокотники/EASTON_SYNERGY GX SOFT_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (261, 'YTH', '', 'Описание Налокотники BAUER NSX YTH, детская любительская модель линейки NSX 2018 года. Налокотники имеют цельную, односоставную конструкцию. Локтевая чаша выполнена из формованного пластика. Для максимального комфорта и более легкого веса в зоне предплечья используется пена средней плотности. Внутренняя подкладка HYDROPHOBIC поглощает влагу и способствует быстрому высыханию. Особенности: • Уровень – Любительский • Посадка - Клиновидная • Защита предплечья – Пена средней плотности • Крепление – Один V-образный • Подкладка – HYDROPHOBIC Характеристики Возрастная группа YTH', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/563/400_400_140cd750bba9870f18aada2478b24840a/760a518b_980f_11e9_aa9a_a4bf0158c9f5_87f72296_99d6_11e9_aa9a_a4bf0158c9f5.jpg?158330257467309', null, 'NSX', 1, 263, 8, 'C:\Users\vinto/images/Налокотники/BAUER_NSX_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (266, 'JR', '', 'Описание Налокотники BAUER VAPOR X800 LITE, полупрофессиональная модель линейки VAPOR 2018 года. Налокотники Х800 LITE имеют клиновидную форму и состоят из 2-х частей. Локти отличаются высоким качеством, обеспечивая защиту и подвижность. Особенности: • Уровень – Полупрофессиональный • Посадка - Клиновидная • Защита локтя – MD FOAM • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – MD FOAM(пена средней плотности) со вставкой из полиэтилена • Крепление – Три ремня крепления • Подкладка –HYDROPHOBIC; SANITIZED®. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e65/400_400_140cd750bba9870f18aada2478b24840a/6afdb0d7_8666_11e8_995e_3085a97fbce9_6afdb0d8_8666_11e8_995e_3085a97fbce9.jpg?158327602565565', null, 'VAPOR X800 LITE', 1, 268, 8, 'C:\Users\vinto/images/Налокотники/BAUER_VAPOR X800 LITE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (270, 'YTH', '', 'Описание Налокотники CCM SUPER TACKS AS1 YTH, детская профессиональная модель линейки CCM 2019 года. Данные налокотники разработаны специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов. Так же для защиты локтя использована пена D3O, которая поглощает энергию. Область бицепса выполнена из пены низкой плотности, которая обеспечивает максимальный комфорт и подвижность. В области предплечья для максимальной защиты и удобства использована пена средней плотности Основные особенности: ✅Уровень – Профессиональный ✅Защита локтя – JDP; D3O ✅Защита предплечья – Пена средней плотности ✅Защита бицепса – Пена низкой плотности Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/989/400_400_140cd750bba9870f18aada2478b24840a/54e268d5_78a6_11e9_aa99_a4bf0158c9f5_bdeff031_79b5_11e9_aa99_a4bf0158c9f5.jpg?158331248073787', null, 'SUPER TACKS AS1', 2, 272, 8, 'C:\Users\vinto/images/Налокотники/CCM_SUPER TACKS AS1_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (272, 'JR', '', 'Описание Налокотники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Данные налокотники имеют двухсоставную конструкцию, для лучшей мобильности и удобства. Стратегически важные места налокотника, такие как, бицепс, предплечье и локтевая чаша выполнена из вспененного полиэтилена, который обеспечивает высокий уровень защиты. Система фиксации налокотника состоит из трех ремней, для повышения комфорта. Особенности: • Уровень – Полупрофессиональный • Защита локтя – JDP; Формованный полиэтилен • Защита бицепса – Полиэтилен • Защита предплечья – Полиэтилен • Крепление – Три ремня крепления • Подкладка – Влагоотталкивающая Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/741/400_400_140cd750bba9870f18aada2478b24840a/d81e554f_af3b_11e8_9963_3085a97fbce9_d81e555a_af3b_11e8_9963_3085a97fbce9.jpg?158330791865548', null, 'JETSPEED FT370', 2, 274, 8, 'C:\Users\vinto/images/Налокотники/CCM_JETSPEED FT370_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (276, 'YTH', '', 'Описание Налокотники BAUER SUPREME 2S PRO YTH, детская профессиональная модель линейки SUPREME 2019 года. Данная модель имеет цельную конструкцию для защиты локтя с пенополиуретановым покрытием. Для защиты бицепса используется пена VENT ARMOR, которая придает налокотнику более легкий вес и хорошо поглощают энергию удара. Для защиты предплечья используется пена средней плотности с вставкой из полиэтилена. В данной модели используется рукав, который придает налокотнику максимальное удобство во время игры. Внутренняя подкладка выполнена по технологии THERMO MAX+, благодаря этому налокотник быстро сохнет. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пенополиуретан ✅Защита бицепса – VENT ARMOR ✅Защита предплечья – Пена средней плотности; полиэтилен ✅Крепление – Рукав; 1 V-образная липучка ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/624/400_400_140cd750bba9870f18aada2478b24840a/628b61fb_763a_11e9_aa99_a4bf0158c9f5_68648833_99d6_11e9_aa9a_a4bf0158c9f5.jpg?158330706858780', null, 'SUPREME 2S PRO', 1, 278, 8, 'C:\Users\vinto/images/Налокотники/BAUER_SUPREME 2S PRO_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (277, 'JR', '', 'Описание Налокотники BAUER VAPOR X900 LITE, профессиональная модель линейки VAPOR 2018 года. Налокотники Х900 LITE имеют клиновидную форму и состоят из 2-х частей. Локти сделана из пены HYPERLITE, которая имеет легкий вес и при этом не теряет своих защитных функций. Внутренний материал налокотника THERMO MAX обработан по технологии SANITIZED®, который предотвращает распространение бактерий и возникновения неприятных запахов. Особенности: • Уровень – Профессиональный • Посадка - Клиновидная • Защита локтя – Пена HYPERLITE • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – Литая пена HYPERLITE • Крепление – Три ремня крепления • Подкладка –THERMO MAX; SANITIZED®. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3b6/400_400_140cd750bba9870f18aada2478b24840a/68b5a796_6ae3_11e8_995b_3085a97fbce9_05ea5bcd_6fb9_11e8_995c_3085a97fbce9.jpg?158331064263313', null, 'VAPOR X900 LITE', 1, 279, 8, 'C:\Users\vinto/images/Налокотники/BAUER_VAPOR X900 LITE_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (280, 'JR', '', 'Описание Налокотники BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Посадка - Анатомическая ✅Защита локтя – Пена высокой плотности ✅Защита бицепса – Пена высокой плотности ✅Защита предплечья – Пена высокой плотности ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/43f/400_400_140cd750bba9870f18aada2478b24840a/854120b1_7639_11e9_aa99_a4bf0158c9f5_70181328_a033_11e9_aa9a_a4bf0158c9f5.jpg?158331474361839', null, 'SUPREME S29', 1, 282, 8, 'C:\Users\vinto/images/Налокотники/BAUER_SUPREME S29_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (282, 'SR', '', 'Описание Налокотники CCM JETSPEED FT370, полупрофессиональная модель линейки CCM 2018 года. Данные налокотники имеют двухсоставную конструкцию, для лучшей мобильности и удобства. Стратегически важные места налокотника, такие как, бицепс, предплечье и локтевая чаша выполнена из вспененного полиэтилена, который обеспечивает высокий уровень защиты. Система фиксации налокотника состоит из трех ремней, для повышения комфорта. Особенности: • Уровень – Полупрофессиональный • Защита локтя – JDP; Формованный полиэтилен • Защита бицепса – Полиэтилен • Защита предплечья – Полиэтилен • Крепление – Три ремня крепления • Подкладка – Влагоотталкивающая Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/169/400_400_140cd750bba9870f18aada2478b24840a/d81e554e_af3b_11e8_9963_3085a97fbce9_d81e555e_af3b_11e8_9963_3085a97fbce9.jpg?158330798665548', null, 'JETSPEED FT370', 2, 284, 8, 'C:\Users\vinto/images/Налокотники/CCM_JETSPEED FT370_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (419, 'SR', '', 'Описание Перчатки CCM TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Перчатки TACKS 9060 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из легкого и прочного полиэстера. Защита пальцев и тыльной стороны перчатки выполнена из пены двойной плотности, и полиэтиленовых вставок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Полиэстер • Защита тыльной стороны –Пена двойной плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –NASH • Подкладка – POLYGIENE Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/4b2/400_400_140cd750bba9870f18aada2478b24840a/9c73660d_789e_11e9_aa99_a4bf0158c9f5_154138cc_99e2_11e9_aa9a_a4bf0158c9f5.jpg?158329773671198', null, 'TACKS 9060', 2, 421, 12, 'C:\Users\vinto/images/Перчатки/CCM_TACKS 9060_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (284, 'SR', '', 'Описание Налокотники BAUER NEXUS 8000 профессионального уровня имеют двухсоставную, подвижную конструкцию с независимым протектором защиты бицепса, что обеспечивает максимально полный диапазон движения руки хоккеиста. Элемент защиты бицепса имеет внутреннюю набивку из пены EPP SuperLite, которая обладает наилучшими защитными характеристиками и сниженным, по сравнению с другими пеноматериалами, весом. Элемент защиты бицепса, выполненный по технологии Dome Protection, полностью армирован пластиковыми вставками, имеет сегментированное строение и обеспечивает максимально полный обхват верхней части руки. Протектор защиты предплечья соединен с локтевой чашей, выполнен из пены EPP SuperLite и дополнительно усилен ребристой, пластиковой вставкой. Предварительно спрофилированная локтевая чаша с мягким, внутренним наполнением снижает ударную нагрузку, приходящуюся на локтевой сустав при падении или столкновении, при ударах. Внутренняя подкладка выполнена из гидрофобного материала по технологии 37.5, которая позволяет игроку тратить больше энергии при движении, удаляя горячий воздух из налокотников и способствуя испарению излишней влаги, за счет повышения температуры тела хоккеиста. Система фиксации налокотника состоит из эластичного ремня шириной 3,8 см, расположенного в верхней части, нейлонового ремня шириной 5 см, фиксирующего нижнюю часть, и ремня из неопрена в средней части налокотника. Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a26/400_400_140cd750bba9870f18aada2478b24840a/1a3cd225_0bd1_11e6_9fde_001fc6b58537_954f6694_17f6_11e6_be99_b0939a30c1de.jpg?158329805144744', null, 'NEXUS 8000', 1, 286, 8, 'C:\Users\vinto/images/Налокотники/BAUER_NEXUS 8000_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (286, 'JR', '', 'Описание Налокотники CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет легкий вес. Для защиты бицепса используется пена высокой плотности и формованный полиэтилен. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM, которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита локтя – JDP; Формованный полиэтилен • Защита бицепса – Формованный полиэтилен • Защита предплечья – Формованный полиэтилен • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f77/400_400_140cd750bba9870f18aada2478b24840a/d81e5551_af3b_11e8_9963_3085a97fbce9_fe43db10_af54_11e8_9963_3085a97fbce9.jpg?158331033665764', null, 'JETSPEED FT390', 2, 288, 8, 'C:\Users\vinto/images/Налокотники/CCM_JETSPEED FT390_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (288, 'SR', '', 'Описание Налокотники WARRIOR ALPHA DX3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы – не стесняет движения и сохраняет высочайший уровень защиты • PREMIUM PROTECTION – Защитная пена и пластиковые вставки, в стратегически важных местах, обеспечивают защиту • PREMIUM STRAPS - Регулируемые ремни обеспечивают надежную фиксацию и удобство • 3-PIECE – Новая трехкомпонентная конструкция, обеспечивает естественное движение и максимальную подвижность локтя • LINER – Подкладка обеспечивает сухость Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/581/400_400_140cd750bba9870f18aada2478b24840a/f0eb3018_b90d_11e9_aa9a_a4bf0158c9f5_699256ee_bbb4_11e9_aa9a_a4bf0158c9f5.jpg?158330847759001', null, 'ALPHA DX3', 3, 290, 8, 'C:\Users\vinto/images/Налокотники/WARRIOR_ALPHA DX3_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (290, 'JR', '', 'Описание Налокотники BAUER NEXUS 1N модель топ уровня отличается профессиональными, защитными свойствами, высокой мобильностью и имеет традиционную посадку. Двухсоставная конструкция налокотников с асимметричной, локтевой чашей способствует удобной посадке и дает игроку естественную свободу движений. Плавающий элемент защиты бицепса сегментированного строения создает максимальный охват верхней части руки. Эффективную защиту бицепса обеспечивает формованная пена высокой плотности. Пластиковые вкладки Dome Protection в элементах защиты бицепса, выполненные в форме купола, закрывают чувствительный локтевой сустав по бокам. Локтевая чаша выполнена из литого пластика и покрыта пеной HF. С внутренней стороны в локтевой чаше используется формованная пена, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. Элемент защиты предплечья выполнен из пены с технологией Seven+, которая в сочетании с пеной высокой плотности и формованным пластиковым вкладышем создает надежную защиту предплечья и образует легкую и воздухопроницаемую конструкцию. При изготовлении внутренней подкладки используется технология 37.5, которая способствует быстрому испарению влаги и высыханию подкладки за счет повышения температуры тела игрока при активном движении. Специальная обработка материала подкладки исключает появление неприятных запахов. Внутренняя часть локтевой чаши с отверстием для локтя исключает смещение налокотников при движении рук. Система крепления налокотника включает в себя нейлоновый ремень шириной 2" (5 см) на предплечье, эластичный ремень шириной 2" (5 см) на бицепсе и эластичный Y-образный ремень из лайкры, фиксирующий налокотник на локтевом сгибе. Характеристики Возрастная группа JR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/6c8/400_400_140cd750bba9870f18aada2478b24840a/c0365f10_4cb9_11e6_88ad_e0cb4ee6cf35_83000ea1_4de8_11e6_9c01_3085a97fbce9.jpg?158331028852181', null, 'NEXUS 1N', 1, 292, 8, 'C:\Users\vinto/images/Налокотники/BAUER_NEXUS 1N_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (350, 'SR', '', 'Описание Наплечник WARRIOR ALPHA DX 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC PROTECTION – Формованная пластина в сочетании с пеной двойной плотности обеспечивает защиту в ключевых зонах • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b13/400_400_140cd750bba9870f18aada2478b24840a/f85e3157_b90b_11e9_aa9a_a4bf0158c9f5_234e424e_bdbd_11e9_aa9a_a4bf0158c9f5.jpg?158331250346779', null, 'ALPHA DX3', 3, 352, 4, 'C:\Users\vinto/images/Нагрудники/WARRIOR_ALPHA DX3_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (293, 'JR', '', 'Описание Налокотники BAUER VAPOR 1X топовая модель в линейке Vapor, новинка 2016 года обладает легкой и подвижной конструкцией, обеспечивая высокий уровень защиты полевого игрока. Налокотники имеют трехсоставную конструкцию Free Flex, которая позволяет элементам защиты бицепса, предплечья и локтевой чаше двигаться гибко и свободно друг от друга во всех направлениях. Элемент защиты бицепса имеет сегментированное строение и навесную конструкцию. В качестве внутреннего наполнения в элементах защиты бицепса используется легкая пена Aerolite, которая покрывает пластиковые вкладыши, эффективно защищая верхнюю часть руки игрока. Встроенная, локтевая чаша из формованного пластика покрыта пеной средней плотности, как с внутренней, так и с наружной стороны, образуя эффективную, двухстороннюю защиту для игрока. Внутреннее отверстие для локтевого сустава в локтевой чаше комфортно фиксирует локтевой сустав, исключая его смещение. Элемент защиты предплечья выполнен из композитного материала Curv и дополнен пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами, не впитывает влагу. Такое сочетание позволяет облегчить защитное снаряжение и обеспечить максимально высокий уровень защиты. В качестве внутренней подкладки используется гидрофобный, сетчатый материал с технологией 37.5, которая позволяет использовать тепло тела для быстрого испарения влаги. Локтевая чаша дополнительно покрыта с внутренней стороны сеткой, которая обладает хорошей воздухопроницаемостью и усиливает действие технологии 37.5, а так же обеспечивает исключительный уровень комфорт. Налокотники фиксируются на руках с помощью трех ремней крепления. Эластичный ремень шириной 2" (5 см) фиксирует элемент защиты предплечья. Эластичный ремень шириной 2" (5 см) фиксирует элемент защиты на бицепсе. Ремень из эластичной сетки Y-образной формы фиксирует локтевую чашу на сгибе локтя. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/8d2/400_400_140cd750bba9870f18aada2478b24840a/aa6bc9a4_142b_11e6_b103_85ec36a0f0cc_17ac9b1b_2ad4_11e6_beb1_8807972027df.jpg?158331029139636', null, 'VAPOR 1X', 1, 295, 8, 'C:\Users\vinto/images/Налокотники/BAUER_VAPOR 1X_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (294, 'SR', '', 'Описание Налокотники BAUER NEXUS 1N модель топ уровня отличается профессиональными, защитными свойствами, высокой мобильностью и имеет традиционную посадку. Двухсоставная конструкция налокотников с асимметричной, локтевой чашей способствует удобной посадке и дает игроку естественную свободу движений. Плавающий элемент защиты бицепса сегментированного строения создает максимальный охват верхней части руки. Эффективную защиту бицепса обеспечивает формованная пена высокой плотности. Пластиковые вкладки Dome Protection в элементах защиты бицепса, выполненные в форме купола, закрывают чувствительный локтевой сустав по бокам. Локтевая чаша выполнена из литого пластика и покрыта пеной HF. С внутренней стороны в локтевой чаше используется формованная пена, которая производится по технологии Seven+. Данная пена быстро восстанавливается после удара, отлично сохраняет свою форму и защитные свойства после многочисленных ударов, отличается легкостью и хорошей воздухопроницаемостью. Элемент защиты предплечья выполнен из пены с технологией Seven+, которая в сочетании с пеной высокой плотности и формованным пластиковым вкладышем создает надежную защиту предплечья и образует легкую и воздухопроницаемую конструкцию. При изготовлении внутренней подкладки используется технология 37.5, которая способствует быстрому испарению влаги и высыханию подкладки за счет повышения температуры тела игрока при активном движении. Специальная обработка материала подкладки исключает появление неприятных запахов. Внутренняя часть локтевой чаши с отверстием для локтя исключает смещение налокотников при движении рук. Система крепления налокотника включает в себя нейлоновый ремень шириной 2" (5 см) на предплечье, эластичный ремень шириной 2" (5 см) на бицепсе и эластичный Y-образный ремень из лайкры, фиксирующий налокотник на локтевом сгибе. Характеристики Возрастная группа SR Линейка NEXUS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/1c3/400_400_140cd750bba9870f18aada2478b24840a/6edc06df_48bc_11e6_b11e_a9e9ddf2845b_9c228199_54c1_11e6_9c01_3085a97fbce9.jpg?158327773252181', null, 'NEXUS 1N', 1, 296, 8, 'C:\Users\vinto/images/Налокотники/BAUER_NEXUS 1N_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (298, 'JR', '', 'Описание Налокотники BAUER VAPOR 1X LITE, топовая модель линейки VAPOR 2018 года. Одним из главных достижений разработчиков этой модели можно считать снижение веса на 25% относительно предыдущей коллекции. Благодаря легким материалам и новейших технологий налокотники обеспечивают максимальное удобство и мобильность без ущерба защите. Применение в стратегических областях материала CURV® создает эффективную защиту с минимальным весом. Особенности: • Уровень – Элитный • Посадка - Клиновидная • Защита локтя – AEROLITE 2.0 • Защита бицепса – Пена HYPERLITE с вставкой из полимера • Защита предплечья – CURV® • Крепление – Три ремня крепления • Подкладка –37,5®; SANITIZED®. Характеристики Возрастная группа JR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7c3/400_400_140cd750bba9870f18aada2478b24840a/68b5a792_6ae3_11e8_995b_3085a97fbce9_885a84cc_6fa4_11e8_995c_3085a97fbce9.jpg?158331491656472', null, 'VAPOR 1X LITE', 1, 300, 8, 'C:\Users\vinto/images/Налокотники/BAUER_VAPOR 1X LITE_JR.png');
