INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (302, 'JR', '', 'Описание Налокотники BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности с добавлением полиэтилена. В области локтя используется пена IX-FOAM, которая поглощает энергию удара. В качестве внутренней подкладки используется материал THERMO MAX+, который максимально отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита локтя – IX-FOAM ✅Защита бицепса – Пена высокой плотности; Полиэтилен ✅Защита предплечья – Пена высокой плотности; Полиэтилен ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/c16/400_400_140cd750bba9870f18aada2478b24840a/a068b2c5_8db7_11e9_aa99_a4bf0158c9f5_46ac159d_a033_11e9_aa9a_a4bf0158c9f5.jpg?158331140559803', null, 'SUPREME 2S', 1, 304, 8, 'C:\Users\vinto/images/Налокотники/BAUER_SUPREME 2S_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (303, 'JR', '', 'Описание Налокотники CCM JETSPEED FT1, топовая модель линейки CCM 2018 года. Основное преимущество данного налокотника в том, что он имеет очень легкий вес. Для защиты бицепса используется очень легкий композитный материал ROCKETFRAME. Зона предплечья выполнена из пены высокой плотности ZOTEFOAM, которая максимально защищает ваше тело. Локтевая чашка выполнена по технологии JDP, которая придает налокотнику максимальное удобство и имеет очень хорошие защитные свойства. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Элитный • Защита локтя – JDP; Формованная пена PE • Защита бицепса – ROCKETFRAME • Защита предплечья – ZOTEFOAM • Крепление – Три ремня крепления • Подкладка – DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a84/400_400_140cd750bba9870f18aada2478b24840a/843c6478_88c0_11e8_995e_3085a97fbce9_843c6479_88c0_11e8_995e_3085a97fbce9.jpg?158331137859476', null, 'JETSPEED FT1', 2, 305, 8, 'C:\Users\vinto/images/Налокотники/CCM_JETSPEED FT1_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (307, 'SR', '', 'Описание Налокотники BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Налокотники данной модели имеют анатомическую посадку. В данной модели применена технология AMP, которая придает налокотнику максимальную подвижности. Область бицепса и предплечья выполнена из пены высокой плотности с добавлением полиэтилена. В области локтя используется пена IX-FOAM, которая поглощает энергию удара. В качестве внутренней подкладки используется материал THERMO MAX+, который максимально отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Посадка - Анатомическая ✅Защита локтя – IX-FOAM ✅Защита бицепса – Пена высокой плотности; Полиэтилен ✅Защита предплечья – Пена высокой плотности; Полиэтилен ✅Крепление – Три ремня крепления ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a1d/400_400_140cd750bba9870f18aada2478b24840a/b14d00bc_8db7_11e9_aa99_a4bf0158c9f5_a4667d1f_99d7_11e9_aa9a_a4bf0158c9f5.jpg?158331073153760', null, 'SUPREME 2S', 1, 309, 8, 'C:\Users\vinto/images/Налокотники/BAUER_SUPREME 2S_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (311, 'SR', '', 'Описание Налокотники CCM SUPER TACKS LONG, профессиональная модель линейки TACKS 2017 года. Налокотники профессионального уровня обладают удобной посадкой и сниженным весом. Налокотники имеют двухсоставную конструкцию и традиционную посадку. Элемент защиты предплечья выполнены из пены высокой плотности и пены D3O, которая создана для эффективного поглощения ударных нагрузок и обеспечения высокого уровня защиты. Локтевая чаша выполнена по технологии JDP, которая имеет легкую конструкцию и защищает локтевой сустав от прямых ударов.Для защиты бицепса используется формованная пена высокой плотности Особенности: • Уровень – Элитный • Защита локтя – JDP; D3O • Защита бицепса – Пена высокой плотности • Защита предплечья – HD FOAM; D3O • Крепление – Три ремня крепления • Подкладка - Влагоотталкивающая Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/e0f/400_400_140cd750bba9870f18aada2478b24840a/aa32eeb8_1219_11e8_994c_3085a97fbce9_2ee64556_676c_11e8_995a_3085a97fbce9.jpg?158330991553736', null, 'SUPER TACKS LONG', 2, 313, 8, 'C:\Users\vinto/images/Налокотники/CCM_SUPER TACKS LONG_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (318, 'YTH', '', 'Описание Наплечник BAUER SUPREME TOTAL ONE MX3 YTH, детская модель обеспечивает максимальное покрытие и эффективную защиту, имеет подвижную конструкцию и легкий вес. Панель защиты груди имеет сегментированное строение, которое позволяет младшим игрокам свободно двигаться без ущерба для защиты. Внутреннее наполнение передней панели выполнено из пены средней плотности. Центральный щиток, покрывающий и защищающий ключицы, грудину и верхнюю часть живота, имеет плавающую конструкцию и включает в себя пену высокой плотности и легковесную защитную пену Vent Armor, которая благодаря своей перфорированной структуре обладает эффективными защитными свойствами, легким весом и отличной вентиляцией. Окантовка вокруг горловины изготовлена из пены высокой плотности. Формованные пластиковые чаши, закрывающие плечевые суставы имеют удобную анатомическую форму и выстланы мягкой пеной. Односоставные элементы, защищающие бицепс, имеют регулируемую посадку и выполнены из вспененного материала с пластиковыми вставками. Задняя панель имеет мульти-сегментную конструкцию. Верхняя часть спины и элемент защиты позвоночника имеют внутреннюю набивку из пены высокой плотности. Пена средней плотности используется в защитных элементах, закрывающих область почек и нижних ребер. Внутренняя подкладка изготавливается из материала Thermo Max, который быстро отводит влагу и имеет антибактериальную пропитку, препятствуя образованию неприятных запахов. Нагрудник надежно крепится на теле маленького хоккеиста с помощью эластичных ремней, расположенных на талии и на бицепсах. Характеристики Возрастная группа YTH Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/604/400_400_140cd750bba9870f18aada2478b24840a/28f9b6eb_0bd1_11e6_9fde_001fc6b58537_04432636_180c_11e6_be99_b0939a30c1de.jpg?158330874740815', null, 'SUPREME TOTALONE MX3', 1, 320, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME TOTALONE MX3_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (322, 'YTH', '', 'Описание Наплечник CCM JETSPEED FT350, любителская модель линейки CCM 2018 года. Особенности: • Уровень – Любителский • Защита плеч – Пена низкой плотности • Защита груди – Формованные вставки PE • Передняя защита ребер – PE FOAM • Защита позвоночника – Формованные вставки PE • Защита бицепса – Формованные вставки PE Характеристики Возрастная группа YTH Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a5e/400_400_140cd750bba9870f18aada2478b24840a/4f9f93f6_5c25_11e9_99c6_3085a97fbce9_4f9f93f7_5c25_11e9_99c6_3085a97fbce9.jpg?158331039240066', null, 'JETSPEED FT350', 2, 324, 4, 'C:\Users\vinto/images/Нагрудники/CCM_JETSPEED FT350_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (331, 'SR', '', 'Описание Наплечник WARRIOR ALPHA DX 5, любительская модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • PROTECTION – Многослойная пена обеспечивает легкий вес, защиту и подвижность Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/1a7/400_400_140cd750bba9870f18aada2478b24840a/accc9581_1fdf_11ea_aa9a_a4bf0158c9f5_04a93ab5_219d_11ea_aa9a_a4bf0158c9f5.jpg?158328181646133', null, 'ALPHA DX5', 3, 333, 4, 'C:\Users\vinto/images/Нагрудники/WARRIOR_ALPHA DX5_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (335, 'SR', '', 'Описание Наплечник BAUER NSX, любительская модель линейки NSX 2018 года. Наплечник предназначен для игры на любительском уровне. Основные защитные элементы выполнены из пены средней плотности. Защита спины состоит из многосегментной пены с добавлением ударопрочного пластика. Особенности: • Уровень – Любительский • Защита плеч – Пена средней плотности MD • Защита груди - Пена средней плотности MD • Передняя защита ребер – Пена средней плотности MD • Защита спины – Пена средней плотности MD • Защита позвоночника – Пена средней плотности MD; ударопрочный пластик • Защита бицепса – Пена средней плотностью MD • Подкладка – HYDROPHOBIC Характеристики Возрастная группа SR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/698/400_400_140cd750bba9870f18aada2478b24840a/4161da43_85cb_11e8_995e_3085a97fbce9_4161da44_85cb_11e8_995e_3085a97fbce9.jpg?158329584252396', null, 'NSX', 1, 337, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_NSX_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (423, 'SR', '', 'Описание Перчатки CCM TACKS 4R PRO, профессиональная модель линейки TACKS 2018 года. Перчатки имеют классическую посадку с широким местом в области пальцев и запястья. Внешний материал перчаток выполнен из высококачественного полиэстера с вставками из искусственной кожи. Защита пальцев и тыльной стороны перчатки выполнена из пены средней плотности и вставок из пластика. Ладонь выполнена из материала высококачественного материала NASH c дополнительным усилением в зонах повышенного воздействия. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: ✅Уровень – Профессиональный ✅Внешний материал – Высококачественный полиэстер ✅Защита тыльной стороны – Пена средней плотности; Вставки из пластика PE ✅Большой палец – Двухсекционный, подвижный (ТОЛЬКО В РАЗМЕРЕ SR) ✅Ладонь – NASH ✅Подкладка – POLYGIENE Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3c2/400_400_140cd750bba9870f18aada2478b24840a/7a219a88_863a_11e7_b836_e0cb4ee6cf35_075c1dad_a4dd_11e7_9cbb_3085a97fbce9.jpg?158331693882121', null, 'TACKS 4R PRO', 2, 425, 12, 'C:\Users\vinto/images/Перчатки/CCM_TACKS 4R PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (337, 'YTH', '', 'Описание Наплечник CCM SUPER TACKS AS1, детская профессиональная модель линейки TACKS 2019 года. Данный наплечник разработан специально для детей. Данная модель обеспечивает в себе лучшее сочетание веса, материалов и защиты. Плечевые чаши наплечника выполнены из высококачественного полиэтилена, который обеспечивает максимальную защиту при минимальном весе. Для обеспечения высокого уровня защиты в области груди используется пена D3O, которая максимально поглощает энергию удара. Так же область груди имеет подвижную конструкцию, обеспечивая максимальный комфорт. Для защиты позвоночника используется пена высокой плотности. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – Высококачественный полиэтилен ✅Защита ключицы – Пена средней плотности ✅Защита груди – D3O ✅Защита позвоночника – Пена высокой плотности Характеристики Возрастная группа YTH Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/96b/400_400_140cd750bba9870f18aada2478b24840a/4f9f93fd_5c25_11e9_99c6_3085a97fbce9_2d09bfb2_79b9_11e9_aa99_a4bf0158c9f5.jpg?158330850845460', null, 'SUPER TACKS AS1', 2, 339, 4, 'C:\Users\vinto/images/Нагрудники/CCM_SUPER TACKS AS1_YTH.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (342, 'JR', '', 'Описание Наплечник EASTON PRO 10, модель профессионального уровня имеет эргономичную конструкцию, которая обеспечивает подвижность и максимальную защиту. Передняя часть нагрудника имеет трехпанельную конструкцию. В качестве материала для внутреннего наполнения используется сочетание пен различной плотности с усиливающими пластиковыми вкладками, что создает максимальную защиту. Так центральные щитки, закрывающие грудину и позвоночник, выполнены из толстого слоя пены средней плотности с усиливающими вставками из вспененного полиэтилена. Элементы, закрывающие боковую часть груди, съемный элемент, закрывающий брюшную полость, имеют наполнение из легкой, вентилируемой пены двойной плотности с основой из пены E-Cell, которая имеет значительно меньший вес по отношению к обычной пене и активно участвует в вентиляции снаряжения. Область ключиц защищена расширенными элементами, выполненными из толстого слоя пены армированного вспененным полиэтиленом. Плечевые чаши выполнены из формованного, вспененного полиэтилена и выстланы вентилируемой пены двойной плотности. Такая конструкция позволяет поглотить силу удара и снизить болевые ощущения. Положение плечевых чаш можно регулировать с помощью липучки. Элементы защиты бицепса имеют двухсоставную конструкцию, которая регулируется по высоте с помощью липучки, что позволяет подобрать необходимую плотность посадки. Пена средней плотности с формованными пластиковыми вставками используется при изготовлении элементов защиты бицепса. Конструкция задней части нагрудника аналогична фронтальной. Сегментированная задняя часть, заполненная пеной средней плотности и пеной E-Cell, плотно охватывает спину игрока, соединяясь с фронтальной частью с помощью сегментированных элементов, закрывающих и защищающих нижнюю часть ребер и область почек. Регулирование нагрудника в нижней части происходит с помощью крепления на липучке. Ворот выполнен из мягкой пены и покрыт материалом, который контактирует с шеей игрока. Подкладка выполнена из быстро сохнущего материала. Характеристики Возрастная группа JR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/7ba/400_400_140cd750bba9870f18aada2478b24840a/a36784aa_8b86_11e6_9c14_3085a97fbce9_1d4b5477_8c4f_11e6_9c14_3085a97fbce9.jpg?158331255040027', null, 'PRO 10', 12, 344, 4, 'C:\Users\vinto/images/Нагрудники/EASTON_PRO 10_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (343, 'JR', '', 'Описание Наплечник WARRIOR ALPHA DX 3, полупрофессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC PROTECTION – Формованная пластина в сочетании с пеной двойной плотности обеспечивает защиту в ключевых зонах • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • LINER – Желтая подкладка обеспечивает сухость Характеристики Возрастная группа JR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/0e7/400_400_140cd750bba9870f18aada2478b24840a/1992894a_b90c_11e9_aa9a_a4bf0158c9f5_1c073c20_bdbd_11e9_aa9a_a4bf0158c9f5.jpg?158331028453744', null, 'ALPHA DX3', 3, 345, 4, 'C:\Users\vinto/images/Нагрудники/WARRIOR_ALPHA DX3_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (346, 'JR', '', 'Описание Наплечник BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена по сравнению с предыдущей коллекция BAUER SUPREME S170, теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения. В качестве внутренней подкладки используется материал THERMO MAX, который отводит влагу. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – Формованная MD FOAM(пена средней плотности) ✅Защита груди – Формованная HD FOAM(пена высокой плотности) ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/70e/400_400_140cd750bba9870f18aada2478b24840a/bf871c2f_7638_11e9_aa99_a4bf0158c9f5_b66d9270_91c8_11e9_aa9a_a4bf0158c9f5.jpg?158331399834813', null, 'SUPREME S29', 1, 348, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME S29_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (351, 'JR', '', 'Описание Наплечник CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости и мобильности. Защита плеч выполнена из пены EVA, которая имеет очень легкий вес и хорошие защитные свойства. Защита груди и позвоночника имеет анатомические, формованные вставки PE SHELLS. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита плеч – EVA FOAM • Защита ключицы – Пластик; плавающая конструкция • Защита груди – PE SHELLS • Передняя защита ребер – Пена высокой плотности • Защита позвоночника – PE SHELLS • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа JR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/a56/400_400_140cd750bba9870f18aada2478b24840a/d81e5532_af3b_11e8_9963_3085a97fbce9_d81e5543_af3b_11e8_9963_3085a97fbce9.jpg?158329691843511', null, 'JETSPEED FT390', 2, 353, 4, 'C:\Users\vinto/images/Нагрудники/CCM_JETSPEED FT390_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (355, 'SR', '', 'Описание Наплечник CCM SUPER TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Для максимальной защиты груди и позвоночника используется пластик. Плечевая чаша выполнена по технологии JDP, предназначенная для рассеивания силы удара. Особенности: ✅Уровень – Полупрофессиональный ✅Защита плеч – JDP ✅Защита ключицы – Пена средней плотности ✅Защита груди – Пластик ✅Передняя защита ребер – Пена средней плотности ✅Защита позвоночника – Пластик ✅Защита бицепса – Пена средней плотности Характеристики Возрастная группа SR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/81e/400_400_140cd750bba9870f18aada2478b24840a/4f9f9404_5c25_11e9_99c6_3085a97fbce9_7cad145b_79b9_11e9_aa99_a4bf0158c9f5.jpg?158331029347386', null, 'TACKS 9060', 2, 357, 4, 'C:\Users\vinto/images/Нагрудники/CCM_TACKS 9060_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (357, 'JR', '', 'Описание Наплечник BAUER SUPREME 2S, профессиональная модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди используется пена высокой плотности, а в качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения, а пена IX-FOAM, которая использована в них служит для максимального поглощения энергии удара. Для максимальной подвижности и комфорта в области бицепса используется конструкция AMP. В качестве внутренней подкладки используется материал THERMO MAX+, который максимально хорошо отводит влагу. Особенности: ✅Уровень – Профессиональный ✅Защита плеч – IX-FOAM ✅Защита груди – Плавающая конструкция из пены высокой плотности ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – Перфорированная пена ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO MAX+ Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/acb/400_400_140cd750bba9870f18aada2478b24840a/78659cad_8db6_11e9_aa99_a4bf0158c9f5_836a498c_91c8_11e9_aa9a_a4bf0158c9f5.jpg?158331046130611', null, 'SUPREME 2S', 1, 359, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME 2S_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (361, 'SR', '', 'Описание Наплечник BAUER SUPREME S17 1S Весной 2017 года Bauer обновил всю модельную линейку защиты Supreme, которая теперь доступна для заказа в России! Нагрудник Supreme 1S гарантирует идеальную анатомическую посадку, что обеспечит вас максимальной энергией в каждом игровом эпизоде. Особенности: • Чашечка: Powerlite • Торс: Vent Armor • Солнечное сплетение: Curv • Подкладка: 37.5 + sanitized В нагруднике Supreme 1S применены композитные вставки из материала CURV с иcпользованием технологии Powerlite. Композитный материал CURV уже знаком по топовым моделям коньков, но технология Powerlite используется впервые. Powerlite это легкая и формуемая пена, обеспечивающая эффект облегания и, как итог, анатомическую посадку нагрудника. В области груди используется дополнительная защита из композитного материала Curv в сочетании с FleXorb. FleXorb задействует пену XRD для непревзойденного поглощения энергии удара. Подкладка 37,5 обеспечивает быстрое высыхание, хорошую вентиляцию и предотвращает размножение бактерий и неприятный запах. Характеристики Возрастная группа SR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/458/400_400_140cd750bba9870f18aada2478b24840a/7a3cd370_0d64_11e7_9c76_3085a97fbce9_444e34fe_242e_11e7_9c82_3085a97fbce9.jpg?158331263550169', null, 'SUPREME 1S', 1, 363, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME 1S_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (362, 'SR', '', 'Описание Наплечник BAUER VAPOR 1X флагманская модель в линейке Vapor, новинка 2016 года, имеет сверхлегкую конструкцию, обладает максимальной подвижностью и высоким уровнем защиты. Нагрудник имеет сегментированную, мультипанельную конструкцию Free Flex. Эластичные соединения между панелями позволяют элементам защиты находиться на тех местах, где они должны быть при активном движении игрока и не смещаться. Центральный щиток на передней панели, закрывающий грудину, выполнен из композитного материала Curv, который обладает высокой прочностью, легкостью, создавая невесомую защиту. Композитный материал дополнен легкой пеной Aerolite, которая на 30% легче стандартной пены высокой плотности, но обладает такими же высокими защитными свойствами. Область ключиц защищена арочными элементами, выполненными из пены высокой плотности, которые движутся за счет эластичных соединений вместе с рукой, создавая надежную защиту. Базовый слой в боковых панелях так же выполнен из пены Aerolite. Съемный элемент защиты живота выполнен из пены средней плотности. Задняя часть нагрудника так же имеет подвижное, сегментированное строение, где центральная часть, закрывающая позвоночник, выполнена из пены высокой плотности. Область почек защищена пеной Aerolite. Регулируемые, двухсоставные элементы защиты бицепса скомбинированы из композитного материала Curv и легкой пены Aerolite. Плечевые чаши сформованы из легкой пены Aerolite, которая эффективно поглощает силу удара, надежно защищая хрупкие суставы. Гидрофобный, сетчатый материал с технологией 37.5 используется в качестве внутренней подкладки. Данный материал позволяет использовать тепло тела для быстрого испарения влаги, обеспечивая быстрый отвод тепла и влаги от тела игрока и создавая исключительный уровень комфорта. Окантовка горловины из мягкой пены обеспечивает комфортное прилегание защитного снаряжения к шее. Эластичные ремни крепления на липучке фиксируют нагрудник на талии и элементы защиты на бицепсе. Характеристики Возрастная группа SR Линейка VAPOR', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/b5b/400_400_140cd750bba9870f18aada2478b24840a/aa6bc9a1_142b_11e6_b103_85ec36a0f0cc_17ac9b27_2ad4_11e6_beb1_8807972027df.jpg?158331086643313', null, 'VAPOR 1X', 1, 364, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_VAPOR 1X_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (364, 'SR', '', 'Описание Наплечник CCM JETSPEED FT390, профессиональная модель линейки CCM 2018 года. Главная особенность данной модели в ее легкости и мобильности. Защита плеч выполнена из пены EVA, которая имеет очень легкий вес и хорошие защитные свойства. Защита груди и позвоночника имеет анатомические, формованные вставки PE SHELLS. В качестве внутренней подкладки используется DRYFOAM , которая эффективно отталкивает влагу. Особенности: • Уровень – Профессиональный • Защита плеч – EVA FOAM • Защита ключицы – Пластик; плавающая конструкция • Защита груди – PE SHELLS • Передняя защита ребер – Пена высокой плотности • Защита позвоночника – PE SHELLS • Защита бицепса – Пена высокой плотности • Подкладка - DRYFOAM Характеристики Возрастная группа SR Линейка JETSPEED', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/79c/400_400_140cd750bba9870f18aada2478b24840a/d81e5531_af3b_11e8_9963_3085a97fbce9_d81e5547_af3b_11e8_9963_3085a97fbce9.jpg?158330821943511', null, 'JETSPEED FT390', 2, 366, 4, 'C:\Users\vinto/images/Нагрудники/CCM_JETSPEED FT390_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (366, 'SR', '', 'Описание Наплечник WARRIOR ALPHA DX PRO , профессиональная модель новой линейки ALPHA 2019 года. • DINAMIC STRIKE – Совершенно новый крой и материалы грудной части: лучше сидит по фигуре, практически не стесняет движения и сохраняет высочайший уровень защиты • DINAMIC CAP – Защищает плечи от повреждений при различных столкновения и ударах, в то же время снижает риск травмы для другого участника столкновения • DX SHELL- Пена EVA ,спрессованная по технологии SHELL SKIN, добавлена в стратегически важных местах, чтобы обеспечить дополнительный уровень защиты там, где это необходимо больше всего • DINAMIC VENTILATION – Внутренний жилет предотвращает впитывание пота внешними элементами защиты. Не намокая, они остаются легкими, не мешая движениям • WARTECH FNC LINER – Антибактериальная подкладка обеспечивает сухость и отсутствие неприятного запаха Характеристики Возрастная группа SR Линейка ALPHA', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/94e/400_400_140cd750bba9870f18aada2478b24840a/da02d617_b90b_11e9_aa9a_a4bf0158c9f5_0eea72a2_bdbd_11e9_aa9a_a4bf0158c9f5.jpg?158329043349282', null, 'ALPHA DX PRO', 3, 368, 4, 'C:\Users\vinto/images/Нагрудники/WARRIOR_ALPHA DX PRO_SR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (370, 'JR', '', 'Описание Наплечник BAUER SUPREME 2S PRO, топовая модель линейки SUPREME 2019 года. Конструкция данного наплечника была изменена теперь он больше прилегает к телу. В области груди для максимальной защиты используется композитный материал CURV, который придает наплечнику более легкий вес и максимальную защиту. В качестве реберной защиты используется пена VENT ARMOR. Плечевые чаши имеют анатомическую форму, что дает свободу движения, а пена PORON XRD, которая использована в них, служит для максимального поглощения энергии удара. Для максимальной подвижности и комфорта в области бицепса и ребер используется конструкция AMP. В качестве внутренней подкладки используется материал THERMO CORE, который максимально хорошо отводит влагу. Особенности: ✅Уровень – Элитный ✅Защита плеч – PORON XRD ✅Защита груди – CURV ✅Передняя защита ребер – VENT ARMOR ✅Защита спины – VENT ARMOR ✅Защита позвоночника – HD FOAM(пена высокой плотности) ✅Защита ключицы – HD FOAM(пена высокой плотности) ✅Подкладка –THERMO CORE Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/ab4/400_400_140cd750bba9870f18aada2478b24840a/54bb4d8a_8db6_11e9_aa99_a4bf0158c9f5_8b565c2b_91c8_11e9_aa9a_a4bf0158c9f5.jpg?158331042636402', null, 'SUPREME 2S PRO', 1, 372, 4, 'C:\Users\vinto/images/Нагрудники/BAUER_SUPREME 2S PRO_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (371, 'JR', '', 'Описание Наплечник CCM SUPER TACKS AS1, топовая модель линейки CCM 2019 года. Данная модель имеет двухсоставную жилетку для большей мобильности. Новая система регулировки позволяет отрегулировать наплечник по объему. Для защиты груди и позвоночника используется пена высокой плотности и пена D3O, которая максимально поглощает энергию удара. Плечевая чаша выполнена по технологии DUAL CORE с использованием пены D3O и пены высокой плотности. Особенности: ✅Уровень – Элитный ✅Защита плеч – Конструкция DUAL CORE; D3O; пена высокой плотности ✅Защита ключицы – Пена высокой плотности ✅Защита груди – D3O; пена высокой плотности ✅Передняя защита ребер – Пена высокой плотности ✅Защита позвоночника – D3O; пена высокой плотности ✅Защита бицепса – Пена высокой плотности Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/92a/400_400_140cd750bba9870f18aada2478b24840a/db5b5e33_78a5_11e9_aa99_a4bf0158c9f5_0c663c4e_79b9_11e9_aa99_a4bf0158c9f5.jpg?158331068149257', null, 'SUPER TACKS AS1', 2, 373, 4, 'C:\Users\vinto/images/Нагрудники/CCM_SUPER TACKS AS1_JR.png');
