INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (394, 'JR', '', 'Описание Перчатки CCM TACKS 9040, любительская модель линейки CCM 2019 года. Перчатки TACKS 9040 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки полиэстер, который придает перчаткам легкий вес. Защита пальцев и тыльной стороны перчатки выполнена из пенополиэтилена, которая обладает легким весом и хорошими защитными функциями. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Особенности: • Уровень – Любительский • Внешний материал – Полиэстер • Защита тыльной стороны – Пенополиэтилен • Ладонь –NASH Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/f48/400_400_140cd750bba9870f18aada2478b24840a/72a93e84_78a5_11e9_aa99_a4bf0158c9f5_eddf080b_99e1_11e9_aa9a_a4bf0158c9f5.png?1583306892211298', null, 'TACKS 9040', 2, 396, 12, 'C:\Users\vinto/images/Перчатки/CCM_TACKS 9040_JR.png');
