INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (408, 'JR', '', 'Описание Перчатки CCM TACKS 9060, полупрофессиональная модель линейки CCM 2019 года. Перчатки TACKS 9060 имеют анатомическую посадку, которая плотно прилегает к руке в области пальцев и расширяется к запястью. Это позволяет игрокам иметь превосходный контроль и отличную мобильность. Внешний материал перчатки изготовлен из легкого и прочного полиэстера. Защита пальцев и тыльной стороны перчатки выполнена из пены двойной плотности, и полиэтиленовых вставок. Ладонь выполнена из материала NASH, которая обладает максимальным удобством и прочностью. Внутренний материал выполнен по технологии POLYGIENE, который предотвращает развитие бактерий и возникновения неприятного запаха. Особенности: • Уровень – Полупрофессиональный • Внешний материал – Полиэстер • Защита тыльной стороны –Пена двойной плотности; Вставки из пластика PE • Большой палец – Двухсекционный, подвижный • Ладонь –NASH • Подкладка – POLYGIENE Характеристики Возрастная группа JR Линейка TACKS', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/af1/400_400_140cd750bba9870f18aada2478b24840a/13c787fa_9814_11e9_aa9a_a4bf0158c9f5_0797c380_99e2_11e9_aa9a_a4bf0158c9f5.jpg?158328344270578', null, 'TACKS 9060', 2, 410, 12, 'C:\Users\vinto/images/Перчатки/CCM_TACKS 9060_JR.png');
INSERT INTO public.product (product_id, age, characteristics, description, src_image_link, link, model, brand_id, rating_id, type_id, image_link) VALUES (414, 'JR', '', 'Описание Перчатки BAUER SUPREME S29, полупрофессиональная модель линейки SUPREME 2019 года. Перчатки имеют анатомическую форму посадки. Манжета выполнена по технологии AMP благодаря чему вы можете свободно двигать запястьем. Ладонь, выполненная из материала AX SUEDE QUATTRO, обеспечивает вам комфорт и позволяет лучше чувствовать клюшку. Внутренняя подкладка THERMO MAX и подкладка в области пальцев HYPERSENSE сохраняют ваши руки в сухости и придают максимальный комфорт. Особенности: • Уровень – Полупрофессиональный • Удобство – Анатомическое • Внешний материал – Плетеная сетка CABLE MESH; Нейлон • Защита тыльной стороны – Пена двойной плотности; вставки из пластика • Защита пальцев – Пена средней плотности; вставки из пластика • Большой палец – Запатентованная технология FLEX-LOCK (состоит из 2-х частей) • Ладонь – AX SUEDE QUATTRO • Подкладка – THERMO MAX; HYPERSENSE Характеристики Возрастная группа JR Линейка SUPREME', '//opt-1274418.ssl.1c-bitrix-cdn.ru/upload/resize_cache/iblock/3c1/400_400_140cd750bba9870f18aada2478b24840a/6710852c_7638_11e9_aa99_a4bf0158c9f5_2c7a423e_8800_11ea_aa9e_a4bf0158c9f5.jpg?158794471781128', null, 'SUPREME S29', 1, 416, 12, 'C:\Users\vinto/images/Перчатки/BAUER_SUPREME S29_JR.png');